INSERT INTO `oc_product_description` VALUES ('155', '2', 'α-LIPOIC ACID &amp; COQ10 CAPSULE', '&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&amp;alpha;-Lipoic Acid &amp;amp; CoQ10 CAPSULE th&amp;uacute;c đẩy sự trao đổi chất để đốt chất b&amp;eacute;o v&amp;agrave; giải độc&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Th&amp;ocirc;ng tin sản phẩm: Hai th&amp;agrave;nh phần ch&amp;iacute;nh, &amp;alpha;-Lipoic Acid v&amp;agrave; CoQ10 th&amp;uacute;c đẩy sự trao đổi chất để gi&amp;uacute;p cơ thể hấp thụ chất dinh dưỡng từ thực phẩm v&amp;agrave; cũng như c&amp;ocirc;ng việc chống oxy ho&amp;aacute; một c&amp;aacute;ch hiệu quả, do vậy dẫn đến đốt chất b&amp;eacute;o v&amp;agrave; giải độc cho sức khỏe v&amp;agrave; vẻ đẹp của bạn trở n&amp;ecirc;n ho&amp;agrave;n hảo, đặc biệt n&amp;ecirc;n d&amp;ugrave;ng cho độ tuổi trung ni&amp;ecirc;n. Nhờ th&amp;agrave;nh phần kh&amp;aacute;c như Ornithine, Citrulline v&amp;agrave; Squalene, việc sản xuất năng lượng được tăng tốc v&amp;agrave; phục hồi nhanh ch&amp;oacute;ng.&lt;br /&gt;\r\nLiều lượng: 4 vi&amp;ecirc;n / ng&amp;agrave;y&lt;/span&gt;&lt;/p&gt;\r\n', 'α-Lipoic Acid &amp; CoQ10 CAPSULE thúc đẩy sự trao đổi chất để đốt chất béo và giải độc. Hai thành phần chính, α-Lipoic Acid và CoQ10 thúc đẩy sự trao đổi chất để giúp cơ thể hấp thụ chất dinh dưỡng từ thực phẩm và cũng như công việc chống oxy...', 'α-LIPOIC ACID &amp; COQ10 CAPSULE, Haba, Supplement, Thực phẩm chức năng, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp', 'α-LIPOIC ACID &amp; COQ10 CAPSULE, Haba, Supplement, Thực phẩm chức năng, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp');
INSERT INTO `oc_product_description` VALUES ('161', '2', 'VITAMIN 2000CXB', '&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;VITAMIN 2000CXB Vitamin C 3000mg per stick / water soluble Vitamins&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Product Information: &amp;nbsp;A single stick contains 2000mg of&amp;nbsp;Vitamin C, the required amount per day to expect some visible and recognizable effects on your body. Vitamin C avoids melanin pigmentation on skin and fights against stress.&amp;nbsp;Vitamin B2, B6, H (Biotin)&amp;nbsp;are formulated in a perfect balance as the complex of water soluble Vitamins, effective for the prevention of pimples and skin roughness.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Intake : 1 stick /day&lt;/span&gt;&lt;/p&gt;\r\n', 'VITAMIN 2000CXB Vitamin C 3000mg per stick / water soluble Vitamins', 'Vitamin 2000CXB, Haba, Supplement, Thực phẩm chức năng, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp', 'Vitamin 2000CXB, Haba, Supplement, Thực phẩm chức năng, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp');
INSERT INTO `oc_product_description` VALUES ('164', '2', 'SHARK FIN 4 &amp; GLUCOSAMINE 5', '&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;SHARK FIN 4 &amp;amp; GLUCOSAMINE 5 cho khớp / vấn đề sụn v&amp;agrave; giữ ẩm da&lt;br /&gt;\r\nTh&amp;ocirc;ng tin sản phẩm: C&amp;ocirc;ng thức với Chondroitin v&amp;agrave; Glucosamine với tỷ lệ 4 vs 5, được biết đến như l&amp;agrave; phần v&amp;agrave;ng. Cả hai, cấu tạo m&amp;ocirc; sụn của ch&amp;uacute;ng ta, l&amp;agrave;m việc hiệu quả tr&amp;ecirc;n khớp v&amp;agrave; ngăn ngừa vi&amp;ecirc;m xương khớp. Ngo&amp;agrave;i ra giữ cho l&amp;agrave;n da ẩm bởi Chondroitin khả năng cao trong việc giữ nước, duy tr&amp;igrave; Collagen chống lại c&amp;aacute;c thiệt hại g&amp;acirc;y ra bởi tia UV, kh&amp;ocirc;, l&amp;atilde;o h&amp;oacute;a. Bằng kỹ thuật đặc biệt, ch&amp;uacute;ng t&amp;ocirc;i ph&amp;aacute;t triển vi&amp;ecirc;n thuốc n&amp;agrave;y khử m&amp;ugrave;i miễn ph&amp;iacute;. Đối với vấn đề tr&amp;ecirc;n khớp, sụn của bạn v&amp;agrave; gi&amp;uacute;p cho thanh ni&amp;ecirc;n giữ lại vẻ đẹp.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Liều lượng: 3 vi&amp;ecirc;n / ng&amp;agrave;y&lt;/span&gt;&lt;/p&gt;\r\n', 'SHARK FIN 4 &amp; GLUCOSAMINE 5 cho khớp / vấn đề sụn và giữ ẩm da. Công thức với Chondroitin và Glucosamine với tỷ lệ 4 vs 5, được biết đến như là phần vàng. Cả hai, cấu tạo mô sụn của chúng ta, làm việc hiệu quả trên khớp và ngăn ngừa viêm xương khớp...', 'Shark fin 4 &amp; Glucosamine 5, Haba, Supplement, Thực phẩm chức năng, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp', 'Shark fin 4 &amp; Glucosamine 5, Haba, Supplement, Thực phẩm chức năng, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp');
INSERT INTO `oc_product_description` VALUES ('165', '2', 'CALCIUM &amp; MAGNESIUM TABLET', '&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Vi&amp;ecirc;n uống Calcium &amp;amp; Magnesium cho xương chắc khỏe&lt;br /&gt;\r\nTh&amp;ocirc;ng tin sản phẩm: Cấu tr&amp;uacute;c xương người thường c&amp;oacute; xu hướng giảm đi sự chắc khỏe ở độ tuổi nhất định v&amp;agrave; lo&amp;atilde;ng xương l&amp;agrave; loại bệnh phổ biến ở những phụ nữ lớn tuổi. Cần phải bổ sung Calcium &amp;amp; Magnesium để giữ cho xương chắc khỏe trong thời gian d&amp;agrave;i. Calcium &amp;amp; Magnesium được tạo bởi c&amp;ocirc;ng thức 2:1.Thay v&amp;igrave; uống 1,5 chai sữa th&amp;igrave; bạn chỉ cần uống 5 vi&amp;ecirc;n Calcium &amp;amp; Magnesium để cung cấp đầy đủ canxi cho bộ xương chắc khỏe mỗi ng&amp;agrave;y.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Liều lượng: 5 vi&amp;ecirc;n / ng&amp;agrave;y&lt;/span&gt;&lt;/p&gt;\r\n', 'Viên uống Calcium &amp; Magnesium cho xương chắc khỏe. Cấu trúc xương người thường có xu hướng giảm đi sự chắc khỏe ở độ tuổi nhất định và loãng xương là loại bệnh phổ biến ở những phụ nữ lớn tuổi...', 'Calcium &amp; Magnesium Tablet, Haba, Supplement, Thực phẩm chức năng, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp', 'Calcium &amp; Magnesium Tablet, Haba, Supplement, Thực phẩm chức năng, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp');
INSERT INTO `oc_product_description` VALUES ('166', '2', 'BILBERRY &amp; LUTEIN CAPSULE', '&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Bilberry &amp;amp; Lutein Capsule cho tổng chăm s&amp;oacute;c mắt&lt;br /&gt;\r\n&lt;br /&gt;\r\nTh&amp;ocirc;ng tin sản phẩm: Gi&amp;uacute;p chăm s&amp;oacute;c mắt to&amp;agrave;n diện cả trong ngắn hạn v&amp;agrave; d&amp;agrave;i hạn. Bilberry (từ Bắc &amp;Acirc;u), Lutein, Chrysanthemum Extract v&amp;agrave; Astaxanthin chiến đấu chống lại sự mệt mỏi mắt, kh&amp;ocirc; mắt hoặc mắt đỏ để ngăn ngừa c&amp;aacute;c bệnh về mắt do l&amp;atilde;o h&amp;oacute;a, chẳng hạn như đục thủy tinh thể, tăng nh&amp;atilde;n &amp;aacute;p, tho&amp;aacute;i h&amp;oacute;a điểm v&amp;agrave;ng, vv Vitamin A v&amp;agrave; tiền th&amp;acirc;n của n&amp;oacute; &amp;beta;-carotene c&amp;oacute; thể ngăn ngừa kh&amp;ocirc; mắt v&amp;agrave; qu&amp;aacute;ng g&amp;agrave;. Khuyến kh&amp;iacute;ch cho người d&amp;ugrave;ng m&amp;aacute;y t&amp;iacute;nh, những người y&amp;ecirc;u th&amp;iacute;ch truyền h&amp;igrave;nh, tr&amp;igrave;nh điều khiển v&amp;agrave; những người y&amp;ecirc;u th&amp;iacute;ch đọc v&amp;agrave; đan.&lt;br /&gt;\r\n&lt;br /&gt;\r\nUống: 2 vi&amp;ecirc;n / ng&amp;agrave;y&lt;/span&gt;&lt;/p&gt;\r\n', 'Bilberry &amp; Lutein Capsule cho tổng chăm sóc mắt. Giúp chăm sóc mắt toàn diện cả trong ngắn hạn và dài hạn. Bilberry (từ Bắc Âu), Lutein, Chrysanthemum Extract và Astaxanthin chiến đấu chống lại sự mệt mỏi mắt, khô mắt hoặc mắt đỏ...', 'Bilberry &amp; Lutein Capsule, Haba, Supplement, Thực phẩm chức năng, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp', 'Bilberry &amp; Lutein Capsule, Haba, Supplement, Thực phẩm chức năng, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp');
INSERT INTO `oc_product_description` VALUES ('167', '2', 'PICACE', '&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Picace cho c&amp;aacute;c vấn đề kinh nguyệt của phụ nữ&lt;br /&gt;\r\n&lt;br /&gt;\r\nTh&amp;ocirc;ng tin sản phẩm: Gồm ba loại chất chống oxy h&amp;oacute;a; Squalene, Vitamin E v&amp;agrave; chất Pycnogenol. Cuối c&amp;ugrave;ng l&amp;agrave; c&amp;aacute;c chiết xuất thực vật lấy từ vỏ c&amp;acirc;y th&amp;ocirc;ng biển của Ph&amp;aacute;p, c&amp;oacute; chứa hơn 40 loại flavonoids để chiến đấu chống lại c&amp;aacute;c enzym hoạt t&amp;iacute;nh g&amp;acirc;y ra bởi căng thẳng l&amp;atilde;o h&amp;oacute;a v&amp;agrave; tia UV. Pic Ace được khuyến kh&amp;iacute;ch cho việc giảm triệu chứng kinh nguyệt của phụ nữ v&amp;agrave; duy tr&amp;igrave; một diện mạo trẻ trung v&amp;agrave; xinh đẹp.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Uống: 2-4 vi&amp;ecirc;n / ng&amp;agrave;y&lt;/span&gt;&lt;/p&gt;\r\n', 'Picace cho các vấn đề kinh nguyệt của phụ nữ. Gồm ba loại chất chống oxy hóa; Squalene, Vitamin E và chất Pycnogenol. Cuối cùng là các chiết xuất thực vật lấy từ vỏ cây thông biển của Pháp, có chứa hơn 40 loại flavonoids để chiến đấu chống lại...', 'Picace, Haba, Supplement, Thực phẩm chức năng, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp', 'Picace, Haba, Supplement, Thực phẩm chức năng, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp');
INSERT INTO `oc_product_description` VALUES ('168', '2', 'JUNKAI &amp; SQUALENE DROPS', '&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Junkai / Squalane Drops cải thiện chức năng gan, hệ miễn dịch v&amp;agrave; độ ẩm cho da&lt;br /&gt;\r\nTh&amp;ocirc;ng tin sản phẩm: Người b&amp;aacute;n HABA l&amp;acirc;u d&amp;agrave;i bổ sung theo kh&amp;aacute;i niệm về &amp;quot;vẻ đẹp Squalane&amp;quot;. Squalene ban đầu tồn tại trong nội tạng con người kh&amp;aacute;c nhau để đ&amp;oacute;ng vai tr&amp;ograve; như chất b&amp;eacute;o chống oxy h&amp;oacute;a v&amp;agrave; được tổng hợp ở gan hay da m&amp;ocirc; của ch&amp;uacute;ng ta để biến đổi th&amp;agrave;nh cholesterol, hormon vv, n&amp;oacute; rất quan trọng đối với cuộc sống của ch&amp;uacute;ng ta. Một số phần sẽ trở th&amp;agrave;nh Squalane l&amp;agrave;m việc để giữ cho l&amp;agrave;n da đẹp. Squalene tinh khiết của ch&amp;uacute;ng t&amp;ocirc;i gi&amp;uacute;p chức năng gan, th&amp;uacute;c đẩy lưu th&amp;ocirc;ng m&amp;aacute;u v&amp;agrave; giữ ẩm da, dẫn đến trẻ trung v&amp;agrave; sống động. Những lợi &amp;iacute;ch tuyệt vời đối với những người ti&amp;ecirc;u thụ rượu được c&amp;ocirc;ng nhận rộng r&amp;atilde;i.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Liều lượng: 4-5 vi&amp;ecirc;n / ng&amp;agrave;y&lt;/span&gt;&lt;/p&gt;\r\n', 'Junkai / Squalane Drops cải thiện chức năng gan, hệ miễn dịch và độ ẩm cho da.  Người bán HABA lâu dài bổ sung theo khái niệm về &quot;vẻ đẹp Squalane&quot;. Squalene ban đầu tồn tại trong nội tạng con người khác nhau để đóng vai trò như chất béo...', 'Junkai / Squalene drops, Haba, Supplement, Thực phẩm chức năng, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp', 'Junkai / Squalene drops, Haba, Supplement, Thực phẩm chức năng, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp');
INSERT INTO `oc_product_description` VALUES ('159', '2', 'BIFIDUS MIX POWDER', '&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;BIFIDUS MIX POWDER cho sức khỏe đường ruột&lt;br /&gt;\r\n&lt;br /&gt;\r\nTh&amp;ocirc;ng tin sản phẩm: Một thanh c&amp;oacute; chứa 15 tỷ Bifidobacterium, cứng rắn chống lại axit dạ d&amp;agrave;y, 3 loại Lactobacilli v&amp;agrave; Oligosaccharide một c&amp;aacute;ch c&amp;acirc;n bằng. Điều khiển c&amp;acirc;n bằng của vi khuẩn trong ruột v&amp;agrave; th&amp;uacute;c đẩy hoạt động của ruột, dẫn đến trạng th&amp;aacute;i khỏe mạnh v&amp;agrave; xinh đẹp của l&amp;agrave;n da v&amp;agrave; cơ thể của bạn. N&amp;oacute; cải thiện m&amp;ocirc;i trường đường ruột ho&amp;agrave;n to&amp;agrave;n loại bỏ c&amp;aacute;c chất độc hại v&amp;agrave; duy tr&amp;igrave; sức khỏe. &lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Uống: 1 thanh / ng&amp;agrave;y&lt;/span&gt;&lt;/p&gt;\r\n', 'BIFIDUS MIX POWDER cho sức khỏe đường ruột. Một thanh có chứa 15 tỷ Bifidobacterium, cứng rắn chống lại axit dạ dày, 3 loại Lactobacilli và Oligosaccharide một cách cân bằng...', 'BIFIDUS MIX POWDER, Haba, Supplement, Thực phẩm chức năng, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp', 'BIFIDUS MIX POWDER, Haba, Supplement, Thực phẩm chức năng, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp');
INSERT INTO `oc_product_description` VALUES ('148', '2', 'PLATINUM SQUA SHAMPOO', '&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;X&amp;agrave; ph&amp;ograve;ng dầu gội đầu l&amp;agrave; c&amp;acirc;u trả lời của ch&amp;uacute;ng t&amp;ocirc;i với m&amp;aacute;i t&amp;oacute;c khỏe mạnh v&amp;agrave; da đầu, với một khả năng l&amp;agrave;m sạch ho&amp;agrave;n hảo v&amp;agrave; kh&amp;ocirc;ng g&amp;acirc;y phản ứng ngứa. Tốt cho một m&amp;aacute;i t&amp;oacute;c mỏng.&lt;/span&gt;&lt;/p&gt;\r\n', 'Xà phòng dầu gội đầu là câu trả lời của chúng tôi với mái tóc khỏe mạnh và da đầu, với một khả năng làm sạch hoàn hảo và không gây phản ứng ngứa. Tốt cho một mái tóc mỏng...', 'Platinum squa shampoo, Haba, Supplement, Thực phẩm chức năng, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp', 'Platinum squa shampoo, Haba, Supplement, Thực phẩm chức năng, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp');
INSERT INTO `oc_product_description` VALUES ('158', '2', 'COLLAGEN &amp; HYALURONIC ACID POWDER', '&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Collagen&amp;amp; Hyaluronic Acid Phấn phủ cho l&amp;agrave;n da bị l&amp;atilde;o h&amp;oacute;a&lt;br /&gt;\r\nTh&amp;ocirc;ng tin sản phẩm: Phấn phủ chống lại sự l&amp;atilde;o h&amp;oacute;a da để c&amp;oacute; 1 l&amp;agrave;n da ho&amp;agrave;n hảo. Collagen v&amp;agrave; Elastin giữ cho l&amp;agrave;n da rắn chắc v&amp;agrave; đ&amp;agrave;n hồi, trong khi Hyaluronic Acid v&amp;agrave; Chondroitin slufate lưu giữ độ ẩm tr&amp;ecirc;n da. Chiết xuất alpina speciosa leaf, Vitamin C, B2, B6 hỗ trợ l&amp;agrave;n da quay trở lại ban đầu.Ngo&amp;agrave;i ra, phấn phủ n&amp;agrave;y l&amp;agrave; sự kết hợp c&amp;aacute;c th&amp;agrave;nh phần c&amp;oacute; độ ẩm, lưu giữ v&amp;agrave; tăng t&amp;iacute;nh đ&amp;agrave;n hồi cho l&amp;agrave;n da bị l&amp;atilde;o h&amp;oacute;a. Với hương vị bưởi ri&amp;ecirc;ng thanh nhẹ.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Liều lượng: 1 tu&amp;yacute;p/ ng&amp;agrave;y&lt;/span&gt;&lt;/p&gt;\r\n', 'Collagen&amp; Hyaluronic Acid Phấn phủ cho làn da bị lão hóa. Phấn phủ chống lại sự lão hóa da để có 1 làn da hoàn hảo. Collagen và Elastin giữ cho làn da rắn chắc và đàn hồi, trong khi Hyaluronic Acid và Chondroitin slufate lưu giữ độ ẩm trên da...', 'Collagen &amp; Hyaluronic Acid Podwer , Haba, Supplement, Thực phẩm chức năng, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp', 'Collagen &amp; Hyaluronic Acid Podwer , Haba, Supplement, Thực phẩm chức năng, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp');
INSERT INTO `oc_product_description` VALUES ('257', '2', 'Full body treatment &amp; bath salt LE', '&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;strong&gt;Full body treatment &amp;amp; bath salt LE: m&amp;ugrave;i chanh&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;color:#ff0066&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;C&amp;ocirc;ng dụng&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Tấy da chết, tẩy da bị chai sờn. L&amp;agrave;m mềm mịn da.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;color:#ff0066&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Hướng dẫn sử dụng&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;u&gt;Sử dụng l&amp;agrave;m Scrub Massage:&lt;/u&gt;&lt;/span&gt;&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;ol&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Để nguy&amp;ecirc;n bịch, d&amp;ugrave;ng tay b&amp;oacute;p đều bịch cho vụn hạt b&amp;ecirc;n trong ra.&lt;/span&gt;&lt;/li&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Sau đ&amp;oacute; lấy ra tay rồi xoa đều l&amp;ecirc;n to&amp;agrave;n th&amp;acirc;n. Lưu &amp;yacute; n&amp;ecirc;n massage nhẹ nh&amp;agrave;ng, tr&amp;aacute;nh t&amp;igrave;nh trạng trầy xước da.&lt;/span&gt;&lt;/li&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Cuối c&amp;ugrave;ng xả lại với nước sạch rồi tiến h&amp;agrave;nh c&amp;aacute;c bước tắm rửa hằng ng&amp;agrave;y&lt;/span&gt;&lt;/li&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Kh&amp;ocirc;ng sử dụng l&amp;ecirc;n mặt&lt;/span&gt;&lt;/li&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Nếu c&amp;ograve;n đọng lại tr&amp;ecirc;n s&amp;agrave;n, lưu &amp;yacute; xả sạch. Tr&amp;aacute;nh t&amp;igrave;nh trạng trượt ng&amp;atilde;.&lt;/span&gt;&lt;/li&gt;\r\n&lt;/ol&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;u&gt;Sử dụng l&amp;agrave;m muối ng&amp;acirc;m bồn:&lt;/u&gt;&lt;/span&gt;&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;ol&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Để nguy&amp;ecirc;n bịch, d&amp;ugrave;ng tay b&amp;oacute;p đều bịch cho vụn hạt b&amp;ecirc;n trong ra.&lt;/span&gt;&lt;/li&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Một bịch sử dụng được cho từ 180-200ml nước. Cho số lượng tương ứng v&amp;agrave;o bồn rồi thư gi&amp;atilde;n to&amp;agrave;n th&amp;acirc;n.&lt;/span&gt;&lt;/li&gt;\r\n&lt;/ol&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;color:#ff0066&quot;&gt;Th&amp;agrave;nh ph&amp;acirc;̀n&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;95% c&amp;aacute;c th&amp;agrave;nh phần chất kho&amp;aacute;ng được khai th&amp;aacute;c từ c&amp;aacute;c quặng kho&amp;aacute;ng tự nhi&amp;ecirc;n ở Nhật Bản.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n', '', '', '');
INSERT INTO `oc_product_description` VALUES ('121', '2', 'DEEP MOISTURE LOTION', '&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Dưỡng ẩm chuy&amp;ecirc;n s&amp;acirc;u một sự sang trọng trong một kết cấu huyết thanh sẽ tan đi tr&amp;ecirc;n da. Chỉ sử dụng cho da kh&amp;ocirc; v&amp;agrave; da bị l&amp;atilde;o h&amp;oacute;a bởi n&amp;oacute; bổ sung độ ẩm ở mức s&amp;acirc;u hơn của da trong khi vẫn giữ n&amp;oacute; ở bề mặt da để c&amp;oacute; 1 l&amp;agrave;n da được dưỡng ẩm l&amp;acirc;u hơn. Platinum nano-colloid, Hyaluronic acid, chiết xuất c&amp;acirc;y xương rồng c&amp;oacute; trong Deep Moisture Lotion gi&amp;uacute;p kh&amp;ocirc;i phục l&amp;agrave;n da bị tổn thương v&amp;agrave; cho l&amp;agrave;n da khả năng tự phục hồi. &lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Hướng dẫ sử dụng: Lấy với k&amp;iacute;ch thước bằng đồng xu cho v&amp;agrave;o l&amp;ograve;ng b&amp;agrave;n tay rồi thoa nhẹ nh&amp;agrave;ng l&amp;ecirc;n to&amp;agrave;n bộ khu&amp;ocirc;n mặt bằng 2 tay. Kh&amp;ocirc;ng được sử dụng b&amp;ocirc;ng cotton hay vỗ l&amp;ecirc;n mặt. Nếu da bị kh&amp;ocirc; th&amp;igrave; lấy 1 lượng lớn hơn ban đầu.&lt;/span&gt;&lt;/p&gt;\r\n', 'Dưỡng ẩm chuyên sâu một sự sang trọng trong một kết cấu huyết thanh sẽ tan đi trên da. Chỉ sử dụng cho da khô và da bị lão hóa bởi nó bổ sung độ ẩm ở mức sâu hơn của da trong khi vẫn giữ nó ở bề mặt da để có 1 làn da được dưỡng ẩm lâu hơn...', 'Deep moisture lotion, Haba, Supplement, Thực phẩm chức năng, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp', 'Deep moisture lotion, Haba, Supplement, Thực phẩm chức năng, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp');
INSERT INTO `oc_product_description` VALUES ('157', '2', 'GINSENG &amp; GINGER TABLET II', '&lt;p&gt;&lt;span style=&quot;font-size:14px&quot;&gt;Ginseng &amp;amp; Ginger Tablet II h&amp;acirc;m n&amp;oacute;ng cơ thể lưu th&amp;ocirc;ng m&amp;aacute;u tốt hơn&lt;br /&gt;\r\nTh&amp;ocirc;ng tin sản phẩm: Tổng cộng 13 th&amp;agrave;nh phần h&amp;acirc;m n&amp;oacute;ng cơ thể, th&amp;iacute;ch hợp cho những người c&amp;oacute; tuần ho&amp;agrave;n m&amp;aacute;u k&amp;eacute;m hoặc một cảm gi&amp;aacute;c lạnh trong m&amp;ugrave;a đ&amp;ocirc;ng hoặc trong một kh&amp;ocirc;ng kh&amp;iacute; lạnh của m&amp;ugrave;a h&amp;egrave;. N&amp;oacute; tăng nhiệt độ cơ thể cho 3 -4 giờ v&amp;agrave; ăn li&amp;ecirc;n tục sẽ cải thiện căn bản t&amp;igrave;nh trạng cơ thể. Chế độ ăn uống, thư gi&amp;atilde;n, phục hồi mệt mỏi v&amp;agrave; tr&amp;aacute;nh cảm lạnh c&amp;oacute; thể được như mong đợi. C&amp;aacute;c th&amp;agrave;nh phần từ ch&amp;acirc;u &amp;Acirc;u v&amp;agrave; ch&amp;acirc;u &amp;Aacute; như Panax notoginseng, Panax ginseng, Kintoki ginger, Cacao, Kudzu (arrowroot) starch, Jujube, vv được chứa.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:14px&quot;&gt;Liều lượng: 5-7 vi&amp;ecirc;n x 1-3 lần / ng&amp;agrave;y&lt;/span&gt;&lt;br /&gt;\r\n&amp;nbsp;&lt;/p&gt;\r\n', 'Ginseng &amp; Ginger Tablet II hâm nóng cơ thể lưu thông máu tốt hơn. Tổng cộng 13 thành phần hâm nóng cơ thể, thích hợp cho những người có tuần hoàn máu kém hoặc một cảm giác lạnh trong mùa đông hoặc trong một không khí lạnh của mùa hè...', 'Ginseng &amp; Ginger Tablet II, Haba, Supplement, Thực phẩm chức năng, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp', 'Ginseng &amp; Ginger Tablet II, Haba, Supplement, Thực phẩm chức năng, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp');
