INSERT INTO `oc_product_description` VALUES ('129', '2', 'POINT REPAIR II', '&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Kiểm so&amp;aacute;t sản xuất b&amp;atilde; nhờn dư thừa v&amp;agrave; ngăn ngừa mụn trứng c&amp;aacute;. Vitamin A, C, E v&amp;agrave; dầu hạt Coix thắt chặt lỗ ch&amp;acirc;n l&amp;ocirc;ng, loại bỏ vết mụn v&amp;agrave; chỉnh sữa kết cấu da cho một cảm ứng mượt, gi&amp;uacute;p trang điểm giữ nguy&amp;ecirc;n vẹn v&amp;agrave;o ban ng&amp;agrave;y. Tạm biệt mụn d&amp;agrave;nh cho người lớn v&amp;agrave; đạt được một l&amp;agrave;n da tươi s&amp;aacute;ng.&lt;br /&gt;\r\nL&amp;agrave;m thế n&amp;agrave;o để sử dụng: Sau 3 bước chăm s&amp;oacute;c da, &amp;aacute;p dụng một k&amp;iacute;ch thước nh&amp;acirc;n đậu đỏ đến c&amp;aacute;c khu vực quan t&amp;acirc;m của bạn.&lt;/span&gt;&lt;/p&gt;\r\n', 'Kiểm soát sản xuất bã nhờn dư thừa và ngăn ngừa mụn trứng cá. Vitamin A, C, E và dầu hạt Coix thắt chặt lỗ chân lông, loại bỏ vết mụn và chỉnh sữa kết cấu da cho một cảm ứng mượt, giúp trang điểm giữ nguyên vẹn vào ban ngày...', 'Point repair II, Haba, Supplement, Thực phẩm chức năng, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp', 'Point repair II, Haba, Supplement, Thực phẩm chức năng, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp');
INSERT INTO `oc_product_description` VALUES ('163', '2', 'SHARK FIN CHONDROITIN POWDER', '&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Shark Fin Chondroitin Powder dạng bột đi c&amp;ugrave;ng với nước uống hoặc thức ăn&lt;br /&gt;\r\nTh&amp;ocirc;ng tin sản xuất: M&amp;ugrave;i hương kiểu dạng bột dễ d&amp;agrave;ng đưa v&amp;agrave;o nước uống hoặc s&amp;uacute;p. Chứa Chondroitin v&amp;agrave; Glucosamine, n&amp;oacute; tồn tại trong sụn, khớp v&amp;agrave; da, gi&amp;uacute;p ngăn ngừa tho&amp;aacute;i h&amp;oacute;a khớp v&amp;agrave; cải thiện c&amp;aacute;c m&amp;ocirc; sụn. Những điều n&amp;agrave;y cũng l&amp;agrave;m mềm da, c&amp;ugrave;ng với Vitamin C đa dạng h&amp;igrave;nh th&amp;agrave;nh. Đối với những người muốn sống một cuộc sống năng động v&amp;agrave; để duy tr&amp;igrave; vẻ đẹp của họ.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Liều lượng: 0.5- 3 g / ng&amp;agrave;y&lt;/span&gt;&lt;/p&gt;\r\n', 'Shark Fin Chondroitin Powder dạng bột đi cùng với nước uống hoặc thức ăn. Mùi hương kiểu dạng bột dễ dàng đưa vào nước uống hoặc súp. Chứa Chondroitin và Glucosamine, nó tồn tại trong sụn, khớp và da, giúp ngăn ngừa thoái hóa khớp và cải thiện...', 'Shark fin chondroitin powder, Haba, Supplement, Thực phẩm chức năng, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp', 'Shark fin chondroitin powder, Haba, Supplement, Thực phẩm chức năng, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp');
INSERT INTO `oc_product_description` VALUES ('143', '2', 'SILKY LATHER SOAP', '&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Da mịn v&amp;agrave; nảy l&amp;ecirc;n ph&amp;ugrave; hợp cho gia đ&amp;igrave;nh sử dụng. V&amp;agrave; cũng tốt cho trẻ sơ sinh.&lt;/span&gt;&lt;/p&gt;\r\n', 'Da mịn và nảy lên phù hợp cho gia đình sử dụng. Và cũng tốt cho trẻ sơ sinh.', 'Sliky lather soap, Haba, Supplement, Thực phẩm chức năng, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp', 'Sliky lather soap, Haba, Supplement, Thực phẩm chức năng, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp');
INSERT INTO `oc_product_description` VALUES ('134', '2', 'WHITE LADY', '&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Serum l&amp;agrave;m đẹp nhanh cho l&amp;agrave;n da trắng. C&amp;ocirc;ng thức nhẹ cho da chỉ với 6% Vitamin C chuyển h&amp;oacute;a, do hợp nhất s&amp;aacute;ng tạo với nước Sasa kurilensis. T&amp;aacute;c dụng tr&amp;ecirc;n những v&amp;ugrave;ng da bị n&amp;aacute;m, bị mụn, nếp nhăn, lỗ ch&amp;acirc;n l&amp;ocirc;ng to hay l&amp;agrave;n da bị kh&amp;ocirc; để c&amp;oacute; một l&amp;agrave;n da tươi s&amp;aacute;ng hơn.&lt;br /&gt;\r\n( Ghi ch&amp;uacute;: WHITE LADY v&amp;agrave; FAIR LADY tại Mỹ l&amp;agrave; 2 sản phẩm c&amp;ugrave;ng loại )&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Hướng dẫn sử dụng: Sau khi rửa mặt sạch, cho 4-5 giọt l&amp;ecirc;n to&amp;agrave;n bộ v&amp;ugrave;ng mặt hoặc phần l&amp;agrave;n da bị tổn thương. Sau đ&amp;oacute; sử dụng tiếp nước hoa hồng.&lt;/span&gt;&lt;/p&gt;\r\n', 'Serum làm đẹp nhanh cho làn da trắng. Công thức nhẹ cho da chỉ với 6% Vitamin C chuyển hóa, do hợp nhất sáng tạo với nước Sasa kurilensis. Tác dụng trên những vùng da bị nám, bị mụn, nếp nhăn...', 'White Lady, Haba, Supplement, Thực phẩm chức năng, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp', 'White Lady, Haba, Supplement, Thực phẩm chức năng, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp');
INSERT INTO `oc_product_description` VALUES ('153', '2', 'VITAMIN ExA', '&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Dầu h&amp;ograve;a tan VITAMIN ExA Vitamin hỗn hợp&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Th&amp;ocirc;ng tin sản phẩm: Chứa đựng 4 loại tự nhi&amp;ecirc;n Vitamin E v&amp;agrave; Vitamin A trong một &amp;nbsp;c&amp;ocirc;ng thức c&amp;acirc;n bằng tốt như sự ho&amp;agrave;n hảo của dầu h&amp;ograve;a tan Vitamin hỗn hợp. &amp;nbsp;Điều n&amp;agrave;y l&amp;agrave; kh&amp;ocirc;ng thể thiếu cho c&amp;ocirc;ng việc l&amp;agrave;m đẹp của ch&amp;uacute;ng ta, th&amp;uacute;c đẩy lưu th&amp;ocirc;ng m&amp;aacute;u, mang dinh dưỡng cho to&amp;agrave;n bộ cơ thể của ch&amp;uacute;ng ta. N&amp;oacute; c&amp;oacute; thể l&amp;agrave;m giảm bờ vai cứng v&amp;agrave; triệu chứng của thời kỳ m&amp;atilde;n kinh. N&amp;oacute; cũng c&amp;oacute; t&amp;aacute;c dụng tr&amp;ecirc;n mắt v&amp;agrave; chất nhầy m&amp;agrave;ng được c&amp;ocirc;ng nhận.&lt;/span&gt;&lt;/p&gt;\r\n', 'Dầu hòa tan VITAMIN ExA Vitamin hỗn hợp.  Chứa đựng 4 loại tự nhiên Vitamin E và Vitamin A trong một  công thức cân bằng tốt như sự hoàn hảo của dầu hòa tan Vitamin hỗn hợp...', 'Vitamin ExA, Haba, Supplement, Thực phẩm chức năng, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp', 'Vitamin ExA, Haba, Supplement, Thực phẩm chức năng, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp');
INSERT INTO `oc_product_description` VALUES ('259', '2', 'Full body treatment &amp; bath salt GF', '&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;strong&gt;Full body treatment &amp;amp; bath salt GF: m&amp;ugrave;i bưởi&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;color:#ff0066&quot;&gt;C&amp;ocirc;ng dụng&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Tấy da chết, tẩy da bị chai sờn. L&amp;agrave;m mềm mịn da.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;color:#ff0066&quot;&gt;Hướng dẫn sử dụng&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;u&gt;Sử dụng l&amp;agrave;m Scrub Massage:&lt;/u&gt;&lt;/span&gt;&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;ol&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Để nguy&amp;ecirc;n bịch, d&amp;ugrave;ng tay b&amp;oacute;p đều bịch cho vụn hạt b&amp;ecirc;n trong ra.&lt;/span&gt;&lt;/li&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Sau đ&amp;oacute; lấy ra tay rồi xoa đều l&amp;ecirc;n to&amp;agrave;n th&amp;acirc;n. Lưu &amp;yacute; n&amp;ecirc;n massage nhẹ nh&amp;agrave;ng, tr&amp;aacute;nh t&amp;igrave;nh trạng trầy xước da.&lt;/span&gt;&lt;/li&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Cuối c&amp;ugrave;ng xả lại với nước sạch rồi tiến h&amp;agrave;nh c&amp;aacute;c bước tắm rửa hằng ng&amp;agrave;y&lt;/span&gt;&lt;/li&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Kh&amp;ocirc;ng sử dụng l&amp;ecirc;n mặt&lt;/span&gt;&lt;/li&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Nếu c&amp;ograve;n đọng lại tr&amp;ecirc;n s&amp;agrave;n, lưu &amp;yacute; xả sạch. Tr&amp;aacute;nh t&amp;igrave;nh trạng trượt ng&amp;atilde;.&lt;/span&gt;&lt;/li&gt;\r\n&lt;/ol&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;u&gt;Sử dụng l&amp;agrave;m muối ng&amp;acirc;m bồn:&lt;/u&gt;&lt;/span&gt;&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;ol&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Để nguy&amp;ecirc;n bịch, d&amp;ugrave;ng tay b&amp;oacute;p đều bịch cho vụn hạt b&amp;ecirc;n trong ra.&lt;/span&gt;&lt;/li&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Một bịch sử dụng được cho từ 180-200ml nước. Cho số lượng tương ứng v&amp;agrave;o bồn rồi thư gi&amp;atilde;n to&amp;agrave;n th&amp;acirc;n.&lt;/span&gt;&lt;/li&gt;\r\n&lt;/ol&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;color:#ff0066&quot;&gt;Th&amp;agrave;nh ph&amp;acirc;̀n&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;95% c&amp;aacute;c th&amp;agrave;nh phần chất kho&amp;aacute;ng được khai th&amp;aacute;c từ c&amp;aacute;c quặng kho&amp;aacute;ng tự nhi&amp;ecirc;n ở Nhật Bản.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n', '', '', '');
INSERT INTO `oc_product_description` VALUES ('162', '2', 'VITAMIN 500CXB', '&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Vitamin C 500mg mỗi vi&amp;ecirc;n / Vitamins h&amp;ograve;a tan trong nước&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Th&amp;ocirc;ng tin sản phẩm: Một vi&amp;ecirc;n thuốc nhai chứa đựng 500mg Vitamin C, c&amp;ugrave;ng với Vitamin P, H ( Biotin ) v&amp;agrave; 7 loại Vitamin B trong một sựu c&amp;acirc;n bằng tốt như hỗn hợp ho&amp;agrave;n hảo của Vitamin h&amp;oacute;a tan. Cho những người muốn giữu một l&amp;agrave;n da đẹp, người hay bị căng thẳng hay người h&amp;uacute;t thuốc l&amp;aacute; v&amp;agrave; để bổ sung Vitamin B khi bạn ăn ki&amp;ecirc;ng hoặc chế độ ăn ki&amp;ecirc;ng kh&amp;ocirc;ng c&amp;acirc;n bằng.&lt;br /&gt;\r\nLiều lượng: 5 vi&amp;ecirc;n / &amp;nbsp;ng&amp;agrave;y&lt;/span&gt;&lt;/p&gt;\r\n', 'Vitamin C 500mg mỗi viên / Vitamins hòa tan trong nước. Một viên thuốc nhai chứa đựng 500mg Vitamin C, cùng với Vitamin P, H ( Biotin ) và 7 loại Vitamin B trong một sựu cân bằng tốt như hỗn hợp hoàn hảo của Vitamin hóa tan...', 'Vitamin 500CXB, Haba, Supplement, Thực phẩm chức năng, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp', 'Vitamin 500CXB, Haba, Supplement, Thực phẩm chức năng, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp');
INSERT INTO `oc_product_description` VALUES ('160', '2', 'VITAMIN 3000CXB', '&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;VITAMIN 3000CXB Vitamin C 3000mg tr&amp;ecirc;n mỗi thanh / Vitamin h&amp;ograve;a tan&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Th&amp;ocirc;ng tin sản phẩm: Mỗi một thanh c&amp;oacute; chưa 3000mg Vitamin C. N&amp;oacute; tương đương với h&amp;agrave;m lượng Vitamin C c&amp;oacute; trong 60 pcs. C&amp;ocirc;ng thức với Vitamin B1, B2, B6, B12, axit Pantothenic v&amp;agrave; Biotin trong việc c&amp;acirc;n bằng tốt nhất của Vitamin h&amp;ograve;a tan hỗn hợp, n&amp;oacute; hoạt động t&amp;iacute;ch cực kh&amp;ocirc;ng chỉ cho c&amp;ocirc;ng việc l&amp;agrave;m đẹp của bạn m&amp;agrave; c&amp;ograve;n duy tr&amp;igrave; sức khỏe của bạn trong những người bận rộn.&lt;br /&gt;\r\nLiều lượng: 1 thanh / ng&amp;agrave;y&lt;/span&gt;&lt;br /&gt;\r\n&amp;nbsp;&lt;/p&gt;\r\n', 'VITAMIN 3000CXB Vitamin C 3000mg trên mỗi thanh / Vitamin hòa tan. Mỗi một thanh có chưa 3000mg Vitamin C. Nó tương đương với hàm lượng Vitamin C có trong 60 pcs. Công thức với Vitamin B1, B2, B6, B12, axit Pantothenic và Biotin trong...', 'Vitamin 3000CXB, Haba, Supplement, Thực phẩm chức năng, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp', 'Vitamin 3000CXB, Haba, Supplement, Thực phẩm chức năng, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp');
INSERT INTO `oc_product_description` VALUES ('125', '4', 'UV WHITE LOTION', '&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Lotion type whitening serum with Vitamin C derivative, good for sensitive skin. Refreshing watery texture of mild formula is absorbed in skin instantly and alleviates skin damages by UV rays. Cushions with moisture and reduces the appearances of dark spots and freckles. &lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;How to use: After face wash, before toner, apply an appropriate amount to the areas of your concern. Then, toner follows. &amp;nbsp;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n', 'Lotion type whitening serum with Vitamin C derivative, good for sensitive skin. Refreshing watery texture of mild formula is absorbed in skin instantly and alleviates skin damages by UV rays...', 'Uv white lotion, Haba, Supplement, Thực phẩm chức năng, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp', 'Uv white lotion, Haba, Supplement, Thực phẩm chức năng, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp');
INSERT INTO `oc_product_description` VALUES ('122', '2', 'VC LOTION', '&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;C&amp;oacute; chứa 2% Vitamin C ph&amp;aacute;t sinh cho t&amp;aacute;c dụng l&amp;agrave;m trắng, trong chất giữ ẩm ban đầu của ch&amp;uacute;ng t&amp;ocirc;i, Ceramide nanosome hoạt động như một loại kem dưỡng ẩm l&amp;acirc;u d&amp;agrave;i. N&amp;oacute; ngăn chặn sản xuất sắc tố melanin để tr&amp;aacute;nh c&amp;aacute;c điểm đen v&amp;agrave; t&amp;agrave;n nhang. Dung dịch nước hoa hồng nhẹ nh&amp;agrave;ng v&amp;agrave; dưỡng ẩm n&amp;agrave;y l&amp;agrave;m cho da mờ hơn v&amp;agrave; rạng rỡ.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;L&amp;agrave;m thế n&amp;agrave;o để sử dụng: Lấy một k&amp;iacute;ch thước lớn đồng xu v&amp;agrave;o l&amp;ograve;ng b&amp;agrave;n tay, thoa l&amp;ecirc;n to&amp;agrave;n bộ khu&amp;ocirc;n mặt của cả hai tay v&amp;agrave; bọc khu&amp;ocirc;n mặt của bạn nhẹ nh&amp;agrave;ng. Lặp lại ứng dụng n&amp;agrave;y năm lần cho hiệu quả tối đa của n&amp;oacute;. Kh&amp;ocirc;ng sử dụng cotton v&amp;agrave; kh&amp;ocirc;ng vỗ nhẹ. &amp;Aacute;p dụng lượng lớn cho đến khi da ho&amp;agrave;n to&amp;agrave;n ngậm nước.&lt;/span&gt;&lt;/p&gt;\r\n', 'Có chứa 2% Vitamin C phát sinh cho tác dụng làm trắng, trong chất giữ ẩm ban đầu của chúng tôi, Ceramide nanosome hoạt động như một loại kem dưỡng ẩm lâu dài. Nó ngăn chặn sản xuất sắc tố melanin để tránh các điểm đen và tàn nhang...', 'Vc lotion, Haba, Supplement, Thực phẩm chức năng, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp', 'Vc lotion, Haba, Supplement, Thực phẩm chức năng, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp');
INSERT INTO `oc_product_description` VALUES ('138', '2', 'SQUA Q10', '&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Coenzyme Q10 v&amp;agrave; Vitamin E được bổ sung với Squalane cho việc chăm s&amp;oacute;c v&amp;agrave; chống oxy ho&amp;aacute; l&amp;atilde;o h&amp;oacute;a hiệu quả. C&amp;oacute; một sức mạnh giữ ẩm mạnh, n&amp;oacute; tăng cường t&amp;iacute;nh đ&amp;agrave;n hồi v&amp;agrave; rạng rỡ của da &amp;nbsp;bằng c&amp;aacute;ch k&amp;iacute;ch hoạt chuyển h&amp;oacute;a da v&amp;agrave; chiến đấu chống lại c&amp;aacute;c nếp nhăn.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;L&amp;agrave;m thế n&amp;agrave;o để sử dụng: Khi da vẫn c&amp;ograve;n ẩm ướt với dung dịch nước hoa hồng, lấy một giọt, trộn đều bằng cả hai tay để &amp;aacute;p dụng cho to&amp;agrave;n bộ khu&amp;ocirc;n mặt nhẹ nh&amp;agrave;ng. Điều chỉnh lượng t&amp;ugrave;y thuộc v&amp;agrave;o t&amp;igrave;nh trạng da, nhưng kh&amp;ocirc;ng &amp;aacute;p dụng &amp;nbsp;nhiều, c&amp;oacute; thể g&amp;acirc;y ra một t&amp;igrave;nh trạng kh&amp;ocirc; da.&lt;/span&gt;&lt;br /&gt;\r\n&amp;nbsp;&lt;/p&gt;\r\n', 'Coenzyme Q10 và Vitamin E được bổ sung với Squalane cho việc chăm sóc và chống oxy hoá lão hóa hiệu quả. Có một sức mạnh giữ ẩm mạnh, nó tăng cường tính đàn hồi và rạng rỡ của da  bằng cách kích hoạt chuyển hóa da và chiến...', 'Squa Q10, Haba, Supplement, Thực phẩm chức năng, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp', 'Squa Q10, Haba, Supplement, Thực phẩm chức năng, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp');
INSERT INTO `oc_product_description` VALUES ('139', '2', 'SQUALANE', '&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Dầu l&amp;agrave;m đẹp cấp cao để giữ cho l&amp;agrave;n da khỏe mạnh, phục hồi l&amp;agrave;n da khỏi những thiệt hại v&amp;agrave; cung cấp cho một l&amp;agrave;n da đẹp. Đ&amp;oacute; l&amp;agrave; một loại dầu trọng lượng nhẹ được hấp thu v&amp;agrave;o da ngay lập tức. Chất lượng tốt nhất trong ng&amp;agrave;nh c&amp;ocirc;ng nghiệp cao tinh khiết đến 99,9% cho việc sử dụng an to&amp;agrave;n ngay cả cho c&amp;aacute;c em b&amp;eacute;.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;L&amp;agrave;m thế n&amp;agrave;o để sử dụng: Khi da vẫn c&amp;ograve;n ẩm ướt với dung dịch nước hoa hồng, lấy một giọt, trộn đều bằng cả hai tay để &amp;aacute;p dụng cho to&amp;agrave;n bộ khu&amp;ocirc;n mặt nhẹ nh&amp;agrave;ng. Điều chỉnh lượng t&amp;ugrave;y thuộc v&amp;agrave;o t&amp;igrave;nh trạng da, nhưng kh&amp;ocirc;ng &amp;aacute;p dụng rất nhiều, trong đ&amp;oacute; c&amp;oacute; thể g&amp;acirc;y ra một t&amp;igrave;nh trạng kh&amp;ocirc; da.&lt;/span&gt;&lt;/p&gt;\r\n', 'Dầu làm đẹp cấp cao để giữ cho làn da khỏe mạnh, phục hồi làn da khỏi những thiệt hại và cung cấp cho một làn da đẹp. Đó là một loại dầu trọng lượng nhẹ được hấp thu vào da ngay lập tức...', 'Squalane, Haba, Supplement, Thực phẩm chức năng, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp', 'Squalane, Haba, Supplement, Thực phẩm chức năng, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp');
INSERT INTO `oc_product_description` VALUES ('214', '2', 'Rosehip Collection', '&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;Lợi &amp;iacute;ch&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Chăm s&amp;oacute;c da tự nhi&amp;ecirc;n nu&amp;ocirc;i dưỡng cho l&amp;agrave;n da đẹp, khỏe mạnh.&lt;br /&gt;\r\nK&amp;iacute;ch thước ho&amp;agrave;n hảo cho chuyến du lịch, thiết lập ho&amp;agrave;n hảo để thử hoặc l&amp;agrave; m&amp;oacute;n qu&amp;agrave; ho&amp;agrave;n hảo cho một người n&amp;agrave;o đ&amp;oacute; m&amp;agrave; bạn tr&amp;acirc;n trọng.&lt;br /&gt;\r\nRosehip Oil Antioxidant + (5ml)&lt;br /&gt;\r\nKem Vital (20ml)&lt;br /&gt;\r\nCream Cleanser (30ml)&lt;br /&gt;\r\nHydrating Mist Toner (20ml)&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;L&amp;agrave;m thế n&amp;agrave;o để &amp;aacute;p dụng&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Xem danh s&amp;aacute;ch những sản phẩm ri&amp;ecirc;ng biệt.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;Th&amp;agrave;nh phần&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Theo thời gian, những thay đổi nhỏ c&amp;oacute; thể xảy ra với danh s&amp;aacute;ch th&amp;agrave;nh phần n&amp;agrave;y. Lu&amp;ocirc;n lu&amp;ocirc;n tham khảo c&amp;aacute;c sản phẩm bao b&amp;igrave;.&lt;br /&gt;\r\nĐể xem những sản phẩm tốt nhất cho bạn, h&amp;atilde;y thử chọn Skincare của ch&amp;uacute;ng t&amp;ocirc;i.&lt;/span&gt;&lt;br /&gt;\r\n&amp;nbsp;&lt;/p&gt;\r\n', 'Chăm sóc da tự nhiên nuôi dưỡng cho làn da đẹp, khỏe mạnh. Kích thước hoàn hảo cho chuyến du lịch, thiết lập hoàn hảo để thử hoặc là món quà hoàn hảo cho một người nào đó mà bạn trân trọng...', 'Rosehip Collection, Trilogy, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp', 'Rosehip Collection, Trilogy, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp');
INSERT INTO `oc_product_description` VALUES ('76', '4', '&quot;un&quot; cover up 11', '&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;strong&gt;M&amp;atilde; m&amp;agrave;u&amp;nbsp;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;strong&gt;00&lt;/strong&gt;: t&amp;ocirc;ng m&amp;agrave;u s&amp;aacute;ng nhất của m&amp;agrave;u trắng tuyết&amp;nbsp;&lt;br /&gt;\r\n&lt;strong&gt;11&lt;/strong&gt;: m&amp;agrave;u nhạt với nền m&amp;agrave;u v&amp;agrave;ng tinh tế&amp;nbsp;&lt;br /&gt;\r\n&lt;strong&gt;22&lt;/strong&gt;: sản phẩm b&amp;aacute;n chạy nhất với nền v&amp;agrave;ng, t&amp;ocirc;ng m&amp;agrave;u n&amp;agrave;y hợp nhất do l&amp;agrave;n da s&amp;aacute;ng vừa.&amp;nbsp;&lt;br /&gt;\r\n&lt;strong&gt;33&lt;/strong&gt;: da m&amp;agrave;u s&amp;aacute;ng nhẹ&amp;nbsp;&lt;br /&gt;\r\n&lt;strong&gt;44&lt;/strong&gt;: m&amp;agrave;u da tối hơn, c&amp;oacute; thể được sử dụng để tạo hiệu ứng m&amp;agrave;u da r&amp;aacute;m nắng hoặc tạo khối cho khu&amp;ocirc;n mặt.&amp;nbsp;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Sản phẩm được nghi&amp;ecirc;n cứu để t&amp;ocirc;n l&amp;ecirc;n v&amp;agrave; phục hồi l&amp;agrave;n da khỏe đẹp, chứ kh&amp;ocirc;ng đơn thuần l&amp;agrave; che đi khuyết điểm. Ho&amp;agrave;n to&amp;agrave;n nhẹ v&amp;agrave; dưỡng ẩm cho da, sản phẩm &amp;quot;un&amp;quot; cover-up sẽ t&amp;ocirc;n l&amp;ecirc;n vẻ trẻ trung v&amp;agrave; tươi s&amp;aacute;ng cho l&amp;agrave;n da, thu nhỏ lỗ ch&amp;acirc;n l&amp;ocirc;ng v&amp;agrave; trẻ h&amp;oacute;a l&amp;agrave;n da. Sử dụng như kem nền hoặc kem che khuyết điểm v&amp;igrave; kem thấm v&amp;agrave;o da một c&amp;aacute;ch r&amp;otilde; rệt gi&amp;uacute;p che đi những khuyết điểm v&amp;agrave; đốm đỏ. Thậm ch&amp;iacute; kem c&amp;ograve;n đem lại một vẻ ngo&amp;agrave;i lung linh m&amp;agrave; kh&amp;ocirc;ng lo lớp trang điểm sẽ tr&amp;ocirc;ng qu&amp;aacute; nặng nề hoặc kh&amp;ocirc; khan. Dưỡng ẩm v&amp;agrave; hồi phục da, đem lại l&amp;agrave;n da khỏe đẹp thứ hai.&lt;br /&gt;\r\nVui l&amp;ograve;ng kh&amp;ocirc;ng sử dụng chất dưỡng ẩm tổng hợp hoặc h&amp;oacute;a học dưới lớp mỹ phẩm rms v&amp;agrave; thay v&amp;agrave;o đ&amp;oacute;, h&amp;atilde;y chọn lựa những sản phẩm c&amp;oacute; nguồn gốc tự nhi&amp;ecirc;n. Sản phẩm dầu l&amp;agrave;m đẹp rms đ&amp;atilde; được chế tạp để cộng hưởng với tất cả c&amp;aacute;c sản phẩm l&amp;agrave;m đẹp của rms, đặc biệt l&amp;agrave; d&amp;ograve;ng sản phẩm &amp;quot;un&amp;quot; cover-up &lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;.20oz/5.67g&amp;nbsp;&lt;/span&gt;&lt;/p&gt;\r\n', 'Sản phẩm được nghiên cứu để tôn lên và phục hồi làn da khỏe đẹp, chứ không đơn thuần là che đi khuyết điểm. Hoàn toàn nhẹ và dưỡng ẩm cho da, sản phẩm &quot;un&quot; cover-up sẽ tôn lên vẻ trẻ trung và tươi sáng cho làn da...', '&quot;Un&quot; cover up 00, &quot;Un&quot; cover up 11, &quot;Un&quot; cover up 22, &quot;Un&quot; cover up 33, &quot;Un&quot; cover up 44, RSM Beauty, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp', '&quot;Un&quot; cover up 00, &quot;Un&quot; cover up 11, &quot;Un&quot; cover up 22, &quot;Un&quot; cover up 33, &quot;Un&quot; cover up 44, RSM Beauty, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp');
