INSERT INTO `oc_product_description` VALUES ('173', '2', 'Mineral Radiance Mask', '&lt;p&gt;&lt;span style=&quot;font-size:18px&quot;&gt;&lt;span style=&quot;color:rgb(255, 0, 153)&quot;&gt;Lợi &amp;iacute;ch&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Kho&amp;aacute;ng &amp;nbsp;s&amp;eacute;t &amp;nbsp;gi&amp;agrave;u &amp;nbsp;cao lanh cung cấp l&amp;agrave;n da rạng rỡ &amp;nbsp;ngay lập tức với một h&amp;agrave;nh động l&amp;agrave;m sạch s&amp;acirc;u. C&amp;oacute; nguồn gốc chiết xuất &amp;nbsp;từ pohutukawa &amp;nbsp;New Zealand gi&amp;uacute;p săn chắc v&amp;agrave; phục hồi trong khi c&amp;aacute;c loại tinh dầu tầm xu&amp;acirc;n l&amp;agrave;m mềm v&amp;agrave; hoa anh thảo nu&amp;ocirc;i dưỡng v&amp;agrave; hydrat h&amp;oacute;a.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:18px&quot;&gt;&lt;span style=&quot;color:rgb(255, 0, 153)&quot;&gt;L&amp;agrave;m thế n&amp;agrave;o để &amp;aacute;p dụng&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&amp;Aacute;p dụng cho mặt v&amp;agrave; cổ l&amp;agrave;m sạch, tr&amp;aacute;nh v&amp;ugrave;ng mắt. Sau đ&amp;oacute; để kh&amp;ocirc; v&amp;agrave; rửa sạch.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;Th&amp;agrave;nh phần&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Aqua (nước), cao lanh, dầu Prunus amygdalus dulcis, Glycerin (rau), tầm xu&amp;acirc;n (Tầm Xu&amp;acirc;n) dầu hạt giống, Titanium dioxide, Cetearyl olivate (v&amp;agrave;) olivate sorbitan, Cetearyl alcohol, Oenothera Biennis dầu hạt giống, Iron Oxide, Simmondsia Chinensis ( Jojoba) Seed Oil, Chamomilla tr&amp;iacute;ch recutita hoa, Metrosideros Excelsa (Pohutukawa) Extract, Daucus dầu carota hạt sativa, Aloe barbadenis l&amp;aacute; chiết xuất, Tocopheryl acetate, Rosmarinus officinalis l&amp;aacute; chiết xuất, Xanthan gum, acid citric, Parfum, acid Dehydroacetic (v&amp;agrave;) benzyl rượu, Benzyl Alcohol, Benzyl benzoat, benzyl salicylate, Cinnamal, Cinnamyl Rượu, citral, Citronellol, Eugenol, Farnesol, Geraniol, Isoeugenol, limonene, linalool&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Theo thời gian, những thay đổi nhỏ c&amp;oacute; thể xảy ra với th&amp;agrave;nh phần dinh dưỡng n&amp;agrave;y. Lu&amp;ocirc;n lu&amp;ocirc;n tham khảo c&amp;aacute;c sản phẩm bao b&amp;igrave;.&lt;br /&gt;\r\nĐể xem những sản phẩm tốt nhất cho bạn, h&amp;atilde;y thử chọn Skincare của ch&amp;uacute;ng t&amp;ocirc;i.&lt;/span&gt;&lt;br /&gt;\r\n&amp;nbsp;&lt;/p&gt;\r\n', 'Khoáng  sét  giàu  cao lanh cung cấp làn da rạng rỡ  ngay lập tức với một hành động làm sạch sâu. Có nguồn gốc chiết xuất  từ pohutukawa  New Zealand giúp săn chắc và phục hồi...', 'Mineral Radiance Mask, Trilogy, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp', 'Mineral Radiance Mask, Trilogy, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp');
