INSERT INTO `oc_product_description` VALUES ('244', '2', 'Organic MHT 10 pcs (green)', '&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;color:#ff0066&quot;&gt;C&amp;ocirc;ng dụng&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Mother Herbs l&amp;agrave; loại tr&amp;agrave; cho c&amp;aacute;c b&amp;agrave; mẹ muốn bảo đảm sức khỏe tốt để chăm s&amp;oacute;c gia đ&amp;igrave;nh v&amp;agrave; con nhỏ của m&amp;igrave;nh. Thay v&amp;igrave; c&amp;aacute;c loại thuốc đắng kh&amp;aacute;c, giới phụ nữ c&amp;oacute; thể lựa chọn cho m&amp;igrave;nh một vị tr&amp;agrave; ph&amp;ugrave; hợp, vừa thư gi&amp;atilde;n vừa tăng cao sức khỏe bản th&amp;acirc;n m&amp;igrave;nh.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;color:#ff0066&quot;&gt;Hướng dẫn sử dụng&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Uống n&amp;oacute;ng: Bỏ t&amp;uacute;i tr&amp;agrave; v&amp;agrave;o cốc, đổ nước n&amp;oacute;ng v&amp;agrave;o chờ từ 3-6 ph&amp;uacute;t sau đ&amp;oacute; để nước tr&amp;agrave; nguội rồi thưởng thức.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Uống lạnh: Cho sẵn đ&amp;aacute; v&amp;agrave;o cốc, bỏ bịch tr&amp;agrave; v&amp;agrave;o rồi đổ nước v&amp;agrave;o chờ h&amp;ograve;a tan rồi thưởng thức.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Kh&amp;ocirc;ng để nơi c&amp;oacute; &amp;aacute;nh s&amp;aacute;ng mặt trời trực tiếp hoặc nhiệt độ qu&amp;aacute; cao.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;color:#ff0066&quot;&gt;Th&amp;agrave;nh ph&amp;acirc;̀n&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;strong&gt;Vị Rasberry: &lt;/strong&gt;C&amp;aacute;c loại l&amp;aacute; dược thảo nguy&amp;ecirc;n chất: th&amp;igrave; l&amp;agrave;, goat rue, l&amp;aacute; hoa hồng, l&amp;aacute; ph&amp;uacute;c bồn tử, l&amp;aacute; bồ c&amp;ocirc;ng anh.&lt;/span&gt;&lt;/p&gt;\r\n', '', '', '');
INSERT INTO `oc_product_description` VALUES ('72', '4', 'Living luminizer', '&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Kh&amp;ocirc;ng c&amp;oacute; g&amp;igrave; so s&amp;aacute;nh được sự kỳ diệu l&amp;agrave;m nổi bật l&amp;agrave;n da của sản phẩm n&amp;agrave;y. C&amp;ocirc;ng thức bao gồm c&amp;aacute;c đ&amp;egrave;n chiếu s&amp;aacute;ng nhằm tạo ra một l&amp;agrave;n da gợi cảm, nhẹ, ph&amp;aacute;t s&amp;aacute;ng. Đ&amp;acirc;y l&amp;agrave; một trong những sản phẩm b&amp;aacute;n chạy nhất của ch&amp;uacute;ng t&amp;ocirc;i v&amp;agrave; cũng l&amp;agrave; một trong những sản phẩm l&amp;agrave;m đẹp &amp;quot;nhất định&amp;quot; phải c&amp;oacute; của ph&amp;aacute;i đẹp. V&amp;agrave; hơn hết, c&amp;aacute;c luminizer sống gi&amp;uacute;p tăng cường sự trẻ trung tự nhi&amp;ecirc;n của da m&amp;agrave; kh&amp;ocirc;ng bị bết d&amp;iacute;nh, nhờn hoặc lấp l&amp;aacute;nh. Kh&amp;ocirc;ng bắt nắng, che được những khuyết điểm mờ, &amp;aacute;nh ngọc trai. Phấn n&amp;agrave;y hợp với mọi t&amp;ocirc;ng da, khiến n&amp;oacute; trở th&amp;agrave;nh một sản phẩm l&amp;agrave;m đẹp n&amp;ecirc;n c&amp;oacute;.&amp;nbsp;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;.17oz/4.82g&amp;nbsp;&lt;/span&gt;&lt;/p&gt;\r\n', 'Nothing compares in magically highlighting the skin. This formula is the ultimate illuminator for creating a sensuous, sheer, luminous glow.\r\nA must have beauty product and our biggest seller.', 'Living luminizer, RSM Beauty, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp', 'Living luminizer, RSM Beauty, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp');
INSERT INTO `oc_product_description` VALUES ('112', '4', 'skin2skin foundation brush', '&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Được thiết kế với c&amp;ocirc;ng nghệ k&amp;eacute;p chưa từng xuất hiện trước đ&amp;acirc;y, cọ trang điểm skin2skin foundation sử dụng đầu l&amp;ocirc;ng với độ cứng kh&amp;aacute;c nhau, được sắp lớp theo h&amp;igrave;nh v&amp;ograve;ng cung để t&amp;aacute;n đều kem nền một c&amp;aacute;ch dễ d&amp;agrave;ng v&amp;agrave; nhanh ch&amp;oacute;ng. L&amp;ocirc;ng cọ d&amp;agrave;y dần từ đầu cọ đến đu&amp;ocirc;i cọ, cho ph&amp;eacute;p sợi cọ chạm đến mọi đường cong tr&amp;ecirc;n khu&amp;ocirc;n mặt. Cọ gi&amp;uacute;p kem thấm v&amp;agrave;o da một c&amp;aacute;ch tự nhi&amp;ecirc;n, che đi lỗ ch&amp;acirc;n l&amp;ocirc;ng m&amp;agrave; kh&amp;ocirc;ng để lại c&amp;aacute;c vệt trang điểm. Cọ RMS Beauty skin2skin được tạo n&amp;ecirc;n bằng sợi tổng hợp chất lượng cao v&amp;agrave; được thiết kế một c&amp;aacute;ch chuy&amp;ecirc;n nghiệp để m&amp;ocirc; phỏng lớp trang điểm tự nhi&amp;ecirc;n như l&amp;agrave;n da thật. Nhờ sợi l&amp;ocirc;ng được sắp xếp một c&amp;aacute;ch hợp l&amp;yacute;, cọ gi&amp;uacute;p kem nền dễ d&amp;agrave;ng thấm s&amp;acirc;u v&amp;agrave;o da, tạo m&amp;agrave;u ch&amp;acirc;n thật từ b&amp;ecirc;n trong l&amp;agrave;n da ra ngo&amp;agrave;i thay v&amp;igrave; chỉ phủ kem l&amp;ecirc;n bề mặt da. Loại cọ n&amp;agrave;y ho&amp;agrave;n to&amp;agrave;n kh&amp;ocirc;ng l&amp;agrave;m từ l&amp;ocirc;ng động vật hay sử dụng bất cử th&amp;agrave;nh phần n&amp;agrave;o từ động vật.&lt;/span&gt;&lt;br /&gt;\r\n&amp;nbsp;&lt;/p&gt;\r\n', 'Được thiết kế với công nghệ kép chưa từng xuất hiện trước đây, cọ trang điểm skin2skin foundation sử dụng đầu lông với độ cứng khác nhau, được sắp lớp theo hình vòng cung để tán đều kem nền một cách dễ dàng và nhanh chóng...', 'Skin2skin foundation brush, RSM Beauty, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp', 'Skin2skin foundation brush, RSM Beauty, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp');
INSERT INTO `oc_product_description` VALUES ('111', '4', 'brightening brush', '&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Một loại vũ kh&amp;iacute; b&amp;iacute; mật gi&amp;uacute;p l&amp;agrave;m nổi bật từng đường n&amp;eacute;t chi tiết tr&amp;ecirc;n khu&amp;ocirc;n mặt. Loại cọ n&amp;agrave;y đặc biệt c&amp;oacute; hiệu quả khi d&amp;ugrave;ng để t&amp;aacute;n kem bắt s&amp;aacute;ng , gi&amp;uacute;p l&amp;agrave;m sắc n&amp;eacute;t từng v&amp;ugrave;ng tr&amp;ecirc;n khu&amp;ocirc;n mặt m&amp;agrave; rất kh&amp;oacute; khi chỉ d&amp;ugrave;ng ng&amp;oacute;n tay. C&amp;acirc;y cọ h&amp;igrave;nh n&amp;oacute;n n&amp;agrave;y l&amp;agrave; dụng cụ ho&amp;agrave;n hảo để l&amp;agrave;m nổi bật phần trong đu&amp;ocirc;i mắt (gần tuyến lệ c&amp;oacute; h&amp;igrave;nh chữ L), sống mũi (tạo h&amp;igrave;nh cho mũi d&amp;agrave;i v&amp;agrave; thon gọn hơn) v&amp;agrave; tim m&amp;ocirc;i (để tạo h&amp;igrave;nh cho đ&amp;ocirc;i m&amp;ocirc;i quyến rũ v&amp;agrave; đầy đặn). Sử dụng c&amp;acirc;y cọ thanh mảnh v&amp;agrave; c&amp;oacute; thể r&amp;uacute;t v&amp;agrave;o n&amp;agrave;y với kem l&amp;agrave;m s&amp;aacute;ng da để l&amp;agrave;m nổi bật c&amp;aacute;c v&amp;ugrave;ng da tr&amp;ecirc;n&lt;/span&gt;&lt;/p&gt;\r\n', 'Một loại vũ khí bí mật giúp làm nổi bật từng đường nét chi tiết trên khuôn mặt. Loại cọ này đặc biệt có hiệu quả khi dùng để tán kem bắt sáng , giúp làm sắc nét từng vùng trên khuôn mặt mà rất khó khi chỉ dùng ngón tay...', 'Brightening brush, RSM Beauty, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp', 'Brightening brush, RSM Beauty, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp');
INSERT INTO `oc_product_description` VALUES ('169', '4', 'skin2skin blush brush', '&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Cọ The skin2skin blush được thiết kế với đầu l&amp;ocirc;ng si&amp;ecirc;u mềm để đảm bảo lượng kem cần thiết từ hộp kem RMS lip2cheek. Khi t&amp;aacute;n cọ The skin2skin blush l&amp;ecirc;n mặt, đầu l&amp;ocirc;ng kh&amp;ocirc;ng x&amp;oacute;a đi kem nền đ&amp;atilde; c&amp;oacute; tr&amp;ecirc;n da, thay v&amp;agrave;o đ&amp;oacute;, m&amp;agrave;u được t&amp;aacute;n thấm v&amp;agrave;o b&amp;ecirc;n trong da để h&amp;ograve;a v&amp;agrave;o lớp trang điểm sẵn c&amp;oacute; để tạo ra hiệu ứng &amp;ldquo;b&amp;agrave;n chải kh&amp;ocirc;ng kh&amp;iacute;&amp;rdquo; chỉ c&amp;oacute; khi tương t&amp;aacute;c trực tiếp từ da đến da. Cọ RMS Beauty skin2skin được tạo n&amp;ecirc;n bằng sợi tổng hợp chất lượng cao v&amp;agrave; được thiết kế một c&amp;aacute;ch chuy&amp;ecirc;n nghiệp để m&amp;ocirc; phỏng lớp trang điểm tự nhi&amp;ecirc;n như l&amp;agrave;n da thật. Nhờ sợi l&amp;ocirc;ng được sắp xếp một c&amp;aacute;ch hợp l&amp;yacute;, cọ gi&amp;uacute;p kem nền dễ d&amp;agrave;ng thấm s&amp;acirc;u v&amp;agrave;o da, tạo m&amp;agrave;u ch&amp;acirc;n thật từ b&amp;ecirc;n trong l&amp;agrave;n da ra ngo&amp;agrave;i thay v&amp;igrave; chỉ phủ kem l&amp;ecirc;n bề mặt da. Loại cọ n&amp;agrave;y ho&amp;agrave;n to&amp;agrave;n kh&amp;ocirc;ng l&amp;agrave;m từ l&amp;ocirc;ng động vật hay sử dụng bất cử th&amp;agrave;nh phần n&amp;agrave;o từ động vật.&lt;/span&gt;&lt;/p&gt;\r\n', 'Cọ The skin2skin blush được thiết kế với đầu lông siêu mềm để đảm bảo lượng kem cần thiết từ hộp kem RMS lip2cheek. Khi tán cọ The skin2skin blush lên mặt, đầu lông không xóa đi kem nền đã có trên da...', 'Skin2skin blush brush, RSM Beauty, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp', 'Skin2skin blush brush, RSM Beauty, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp');
INSERT INTO `oc_product_description` VALUES ('178', '4', 'Make-Up Be Gone Cleansing Balm', '&lt;p&gt;&lt;span style=&quot;font-size:18px&quot;&gt;&lt;span style=&quot;color:rgb(255, 0, 153)&quot;&gt;Bennefits&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;A lush, end-of-the-day cleansing balm to remove foundation, mascara and lipstick like nothing else, even long-wear and waterproof formulations. Packaged with its own sustainably produced, unbleached, organic cotton cloth, this new way to cleanse is the perfect night-time ritual to melt away make-up and nourish your skin.&lt;br /&gt;\r\nCombining a rich blend of pure plant oils, the luxurious balm melts on contact with warm skin, dissolving impurities and leaving your face perfectly cleansed, supple and silky-smooth.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:18px&quot;&gt;&lt;span style=&quot;color:rgb(255, 0, 153)&quot;&gt;How to apply&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Scoop &amp;frac14; teaspoon onto fingertips and massage on to dry skin. As the balm melts, concentrate around eyelashes and areas where of heavy make-up. Rinse the organic cloth with warm water, wring and gently wipe over the entire face. &amp;nbsp;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;Ingredients&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Helianthus annuus seed oil, Olea europaea fruit oil, Cocos nucifera oil, Cera alba, Polyglceryl-3 Palmitate, Mangifera indica (Mango) seed butter, Rosa Canina (rosehip) seed oil, Camellia oleifera seed oil, Tocopheryl acetate, Polyglyceryl-2 Dipolyhydroxystearate, Chamomilla recutita flower extract, Parfum, Citronellol, Limonene, Linalool&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;em&gt;From time to time minor changes may occur to this ingredient list. Always refer to the product packaging.&lt;/em&gt;&lt;/span&gt;&lt;/p&gt;\r\n', 'A lush, end-of-the-day cleansing balm to remove foundation, mascara and lipstick like nothing else, even long-wear and waterproof formulations. Packaged with its own sustainably produced, unbleached, organic cotton cloth...', 'Make-Up Be Gone Cleansing Balm, Trilogy, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp', 'Make-Up Be Gone Cleansing Balm, Trilogy, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp');
INSERT INTO `oc_product_description` VALUES ('180', '4', 'Hydrating Mist Toner', '&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;Lợi &amp;iacute;ch&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;A gentle, aromatic hydration mist to deliver instant moisture to thirsty skin of any type. This refreshing and uplifting mist toner completes any cleansing routine. Or spritz anytime to cool and uplift.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:18px&quot;&gt;&lt;span style=&quot;color:rgb(255, 0, 153)&quot;&gt;L&amp;agrave;m thế n&amp;agrave;o để &amp;aacute;p dụng&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Spray from bottle held at least 30cm from face. Allow mist to dry naturally. Use daily or as often as required.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;Th&amp;agrave;nh phần&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Aqua (water), Aloe barbadenis leaf juice, Rose geranium flower oil, Rose flower water, Lavandula angustifolia oil, Caprylyl/capryl wheat bran/straw glycosides (and) aqua (and) fusel wheat bran/straw glycosides (and) polyglyceryl-5 oleate (and) sodium glutamate (and) glyceryl caprylate, Dehydroacetic acid (and) benzyl alcohol, Geraniol, Citronellol, Linalool&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Theo thời gian, những thay đổi nhỏ c&amp;oacute; thể xảy ra với danh s&amp;aacute;ch th&amp;agrave;nh phần n&amp;agrave;y. Lu&amp;ocirc;n lu&amp;ocirc;n tham khảo c&amp;aacute;c sản phẩm bao b&amp;igrave;.&lt;br /&gt;\r\nĐể xem những sản phẩm tốt nhất cho bạn, h&amp;atilde;y thử chọn Skincare của ch&amp;uacute;ng t&amp;ocirc;i.&lt;/span&gt;&lt;br /&gt;\r\n&amp;nbsp;&lt;/p&gt;\r\n', 'A gentle, aromatic hydration mist to deliver instant moisture to thirsty skin of any type. This refreshing and uplifting mist toner completes any cleansing routine. Or spritz anytime to cool and uplift...', 'Hydrating Mist Toner, Trilogy, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp', 'Hydrating Mist Toner, Trilogy, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp');
INSERT INTO `oc_product_description` VALUES ('181', '2', 'Gentle Facial Exfoliant', '&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;Lợi &amp;iacute;ch&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Loại bỏ c&amp;aacute;c mảnh vỡ bề mặt v&amp;agrave; cải thiện r&amp;otilde; n&amp;eacute;t với điều trị da tẩy tế b&amp;agrave;o chết nhẹ nh&amp;agrave;ng v&amp;agrave; hiệu quả của ch&amp;uacute;ng t&amp;ocirc;i. C&amp;ocirc;ng thức với chiết xuất &amp;nbsp;s&amp;aacute;p c&amp;acirc;y Jojoba tự nhi&amp;ecirc;n, tự ph&amp;acirc;n hủy v&amp;agrave; kh&amp;ocirc;ng g&amp;acirc;y chầy xước, Gentle Facial Exfoliant nhẹ nh&amp;agrave;ng đ&amp;aacute;nh b&amp;oacute;ng da, tinh lọc kết cấu của n&amp;oacute; v&amp;agrave; cải thiện r&amp;otilde; n&amp;eacute;t m&amp;agrave; kh&amp;ocirc;ng g&amp;acirc;y k&amp;iacute;ch ứng hay ảnh hưởng đến chức năng bảo vệ tự nhi&amp;ecirc;n của da.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;L&amp;agrave;m thế n&amp;agrave;o để &amp;aacute;p dụng&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Nhẹ nh&amp;agrave;ng xoa v&amp;agrave;o da ẩm bằng c&amp;aacute;ch sử dụng chuyển động tr&amp;ograve;n. Rửa sạch với nước ấm v&amp;agrave; d&amp;ugrave;ng khăn lau kh&amp;ocirc;. Sử dụng 1-2 lần mỗi tuần hoặc theo y&amp;ecirc;u cầu.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;Th&amp;agrave;nh phần&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Aqua (water), Kaolin, Prunus amygdalus dulcis oil, Glyceryl stearate, Hydrogenated Simmondsia Chinensis (Jojoba) Seed Oil, Glycerin (vegetable), Cetearyl alcohol, Zinc Oxide, Rosa Canina (rosehip) seed oil, Oenothera biennis seed oil, Camellia oleifera seed oil, Tocopheryl acetate, Macadamia ternifolia seed oil, Sodium stearoyl glutamate, Calendula officinalis flower extract (and) glycine soja (soybean) oil, Honey (mel), Citric acid, Dehydroacetic acid (and) benzyl alcohol, Parfum, Geraniol, Citronellol, Linalool&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Theo thời gian, những thay đổi nhỏ c&amp;oacute; thể xảy ra với danh s&amp;aacute;ch th&amp;agrave;nh phần n&amp;agrave;y. Lu&amp;ocirc;n lu&amp;ocirc;n tham khảo c&amp;aacute;c sản phẩm bao b&amp;igrave;.&lt;br /&gt;\r\nĐể xem những sản phẩm tốt nhất cho bạn, h&amp;atilde;y thử chọn Skincare của ch&amp;uacute;ng t&amp;ocirc;i.&lt;/span&gt;&lt;br /&gt;\r\n&amp;nbsp;&lt;/p&gt;\r\n', 'Loại bỏ các mảnh vỡ bề mặt và cải thiện rõ nét với điều trị da tẩy tế bào chết nhẹ nhàng và hiệu quả của chúng tôi. Công thức với chiết xuất  sáp cây Jojoba tự nhiên, tự phân hủy và không gây chầy xước...', 'Gentle Facial Exfoliant, Trilogy, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp', 'Gentle Facial Exfoliant, Trilogy, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp');
