INSERT INTO `oc_product_description` VALUES ('241', '2', 'White Clay', '&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;color:#ff0066&quot;&gt;C&amp;ocirc;ng dụng&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;L&amp;agrave;m sạch bụi bẩn tr&amp;ecirc;n da.&amp;nbsp;Nhờ v&amp;agrave;o chất kho&amp;aacute;ng c&amp;oacute; trong cao lanh, White Clay cung cấp nước v&amp;agrave; dưỡng ẩm rất hiệu quả cho l&amp;agrave;n da.&amp;nbsp;Đặc biệt tốt cho da kh&amp;ocirc;.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;color:#ff0066&quot;&gt;Hướng dẫn sử dụng&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Sử dụng để rửa mặt:&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;ol&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Lấy một muỗng to ra dĩa hoặc ch&amp;eacute;n nhỏ, pha với một muỗng c&amp;agrave; ph&amp;ecirc; nước, khuấy đều.&lt;/span&gt;&lt;/li&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Sau khi đặc sệt lại th&amp;igrave; đắp đều l&amp;ecirc;n mặt. Ch&amp;uacute; &amp;yacute; chừa c&amp;aacute;c v&amp;ugrave;ng quanh mắt v&amp;agrave; miệng, kh&amp;ocirc;ng để d&amp;acirc;y v&amp;agrave;o mắt v&amp;agrave; miệng.&lt;/span&gt;&lt;/li&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;M&amp;aacute;t xa đều tay trong khoảng 1 ph&amp;uacute;t rồi rữa sạch lại với nước.&lt;/span&gt;&lt;/li&gt;\r\n&lt;/ol&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Sử dụng l&amp;agrave;m mặt nạ:&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;ol&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Lập lại bước 1 v&amp;agrave; 2 như tr&amp;ecirc;n&lt;/span&gt;&lt;/li&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Đắp từ 3 tới 5 ph&amp;uacute;t sau đ&amp;oacute; rửa sạch lại với nước&lt;/span&gt;&lt;/li&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;C&amp;oacute; thể th&amp;ecirc;m dầu thực vật hoặc c&amp;aacute;c loại tinh dầu t&amp;ugrave;y nhu cầu chăm s&amp;oacute;c da của mỗi người để tăng cao hiệu quả.&lt;/span&gt;&lt;/li&gt;\r\n&lt;/ol&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;color:#ff0066&quot;&gt;Th&amp;agrave;nh ph&amp;acirc;̀n&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Cao lanh được khai th&amp;aacute;c tại Ph&amp;aacute;p&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n', '', '', '');
INSERT INTO `oc_product_description` VALUES ('182', '2', 'Very Gentle Moisturising Cream', '&lt;p&gt;&lt;span style=&quot;color:#ff00cc&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;Lợi &amp;iacute;ch&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;L&amp;agrave;m dịu v&amp;agrave; chắn bảo vệ l&amp;agrave;n da suy yếu v&amp;agrave; chăm s&amp;oacute;c cho l&amp;agrave;n da dễ bị k&amp;iacute;ch th&amp;iacute;ch với kem dưỡng ẩm s&amp;acirc;u v&amp;agrave; thoải m&amp;aacute;i của ch&amp;uacute;ng t&amp;ocirc;i. Nu&amp;ocirc;i dưỡng v&amp;agrave; tăng sức sống &amp;nbsp;với chăm s&amp;oacute;c đặc biệt &amp;nbsp;n&amp;oacute; cũng gi&amp;uacute;p c&amp;aacute;c đường nhăn v&amp;agrave; nếp nhăn xuất hiện mượt m&amp;agrave; hơn.&lt;br /&gt;\r\nChứa hương liệu, tinh dầu miễn ph&amp;iacute; v&amp;agrave; được thử nghiệm da liễu. Bạn sẽ được chăm s&amp;oacute;c kỹ lưỡng.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;L&amp;agrave;m thế n&amp;agrave;o để &amp;aacute;p dụng&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&amp;Aacute;p dụng để l&amp;agrave;m sạch mặt, cổ v&amp;agrave; ngực. Sử dụng v&amp;agrave;o buổi s&amp;aacute;ng v&amp;agrave; / hoặc buổi tối theo y&amp;ecirc;u cầu.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:18px&quot;&gt;&lt;span style=&quot;color:rgb(255, 0, 153)&quot;&gt;Th&amp;agrave;nh phần&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Aqua (water), Prunus amygdalus dulcis oil, Glycerin (vegetable), Cetearyl olivate (and) sorbitan olivate, Cetearyl alcohol, Phragmites kharka extract and (water) (and) poria cocos extract, Althaea officinalis root extract, Chamomilla recutita flower extract, Sodium hyaluronate, Camellia sinensis leaf extract, Olea europaea fruit oil, Oenothera biennis seed oil, Calendula officinalis flower extract (and) glycine soja (soybean) oil, Xanthan gum, Rosmarinus officinalis leaf extract, Citric acid, Dehydroacetic acid (and) benzyl alcohol&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Theo thời gian, những thay đổi nhỏ c&amp;oacute; thể xảy ra với danh s&amp;aacute;ch th&amp;agrave;nh phần n&amp;agrave;y. Lu&amp;ocirc;n lu&amp;ocirc;n tham khảo c&amp;aacute;c sản phẩm bao b&amp;igrave;.&lt;br /&gt;\r\nĐể xem những sản phẩm tốt nhất cho bạn, h&amp;atilde;y thử chọn Skincare của ch&amp;uacute;ng t&amp;ocirc;i.&lt;/span&gt;&lt;br /&gt;\r\n&amp;nbsp;&lt;/p&gt;\r\n', 'Làm dịu và chắn bảo vệ làn da suy yếu và chăm sóc cho làn da dễ bị kích thích với kem dưỡng ẩm sâu và thoải mái của chúng tôi. Nuôi dưỡng và tăng sức sống  với chăm sóc đặc biệt  nó cũng giúp các đường nhăn và nếp nhăn xuất hiện mượt mà hơn...', 'Very Gentle Moisturising Cream, Trilogy, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp', 'Very Gentle Moisturising Cream, Trilogy, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp');
INSERT INTO `oc_product_description` VALUES ('183', '4', 'Line Smoothing Day Cream', '&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;Benefits&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Formulated with our latest innovation Glycablend&amp;trade;, this concentrated botanical day cream promotes healthy, radiant skin at any age. Visibly smooth fine lines and wrinkles for younger looking skin. The breakthrough formula delivers maximum hydration while helping to restore elasticity for a super-supple look and firmer feel.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;How to apply&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Apply to cleansed face, neck and d&amp;eacute;colletage. Use daily or as required.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;Ingredients&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Aqua (water), Glycerin (vegetable), Cetearyl alcohol, Simmondsia Chinensis (Jojoba) Seed Oil, Cetearyl olivate (and) sorbitan olivate, Theobroma cacao seed butter, Coco-caprylate, Caprylic/capric triglyceride, Butyrospermum parkii, Dipalmitoyl hydroxyproline, Cucumis melo fruit extract, Persea gratissima, Oenothera biennis seed oil, Tocopheryl acetate, Sodium hyaluronate, Euterpe oleracea fruit oil, Potassium cetyl phosphate, Xanthan gum, Rosmarinus officinalis leaf extract, Parfum, Citric acid, Dehydroacetic acid (and) benzyl alcohol, Benzyl salicylate, Citral, Citronellol, Coumarin, Limonene, Eugenol, Geraniol, Linalool&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;em&gt;From time to time minor changes may occur to this ingredient list. Always refer to the product packaging.&lt;/em&gt;&lt;/span&gt;&lt;/p&gt;\r\n', 'Formulated with our latest innovation Glycablend™, this concentrated botanical day cream promotes healthy, radiant skin at any age. Visibly smooth fine lines and wrinkles for younger looking skin...', 'Line Smoothing Day Cream, Trilogy, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp', 'Line Smoothing Day Cream, Trilogy, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp');
INSERT INTO `oc_product_description` VALUES ('184', '4', 'Replenishing Night Cream', '&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;Benefits&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;With a luxurious texture and subtle, sophisticated fragrance, our easily-absorbed nighttime moisturiser deeply nourishes and repairs while you sleep. A potent blend of nourishing bio-actives, including&amp;nbsp;co-enzyme Q10 and our latest innovation Glycablend&amp;trade;, Replenishing Night Cream assists the skin&amp;rsquo;s natural process of regeneration, helping to restore elasticity and suppleness while smoothing fine lines and wrinkles.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;How to apply&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Apply to cleansed skin on face, neck and d&amp;eacute;colletage each night before bed.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;Ingredients&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Aqua (water), Prunus amygdalus dulcis oil, Cetearyl alcohol, Cetearyl olivate (and) sorbitan olivate, Theobroma cacao seed butter, Glycyrrhiza glaba root extract, Morus alba root extract, Cucumis sativa fruit extract, Chamomilla recutita flower extract, Hamamelis virginiana water, Oenothera biennis seed oil, Sodium hyaluronate, Euterpe oleracea fruit oil, Tocopheryl acetate, Sclerocarya birrea seed oil, Olea europaea fruit oil, Persea gratissima, Aloe barbadenis leaf extract, Calendula officinalis flower extract (and) glycine soja (soybean) oil, Rosa Canina (rosehip) seed oil, Camellia sinensis leaf extract, Rosmarinus officinalis leaf extract, Citric acid, Dehydroacetic acid (and) benzyl alc