INSERT INTO `oc_product_description` VALUES ('222', '2', 'UV skin perfection (01 Light beige)', '&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;color:#ff0066&quot;&gt;C&amp;ocirc;ng dụng&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Với một trong những th&amp;agrave;nh phần ch&amp;iacute;nh l&amp;agrave; chất kho&amp;aacute;ng gi&amp;uacute;p tạo m&amp;agrave;ng chống tia UV l&amp;ecirc;n tới SPF32 PA++, Skin Perfection gi&amp;uacute;p tạo một lớp chống nắng d&amp;agrave;y nhưng kh&amp;ocirc;ng cộm da.&amp;nbsp;Skin Perfection chứa kh&amp;aacute; nhiều chất dưỡng b&amp;ecirc;n trong, l&amp;agrave;m nhiệm vụ k&amp;eacute;p vừa dưỡng vừa l&amp;agrave;m BB Cream, với người da nhiều khuyết điểm c&amp;oacute; thể dặm th&amp;ecirc;m một lớp phấn, nhưng nếu kh&amp;ocirc;ng th&amp;igrave; chỉ cần Skin Perfection cũng c&amp;oacute; thể thay thế tốt nhiệm vụ của Foundation.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;Điều đặc biệt ở sản phẩm n&amp;agrave;y&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Hỗn hợp 3 chất tinh dầu (hoa hồng, ph&amp;uacute;c bồn tử, lựu).&amp;nbsp;Mềm, dịu nhẹ v&amp;agrave; c&amp;oacute; m&amp;agrave;u hồng ngọc trai &amp;oacute;ng &amp;aacute;nh đặc biệt.&amp;nbsp;Nhờ chiết xuất c&amp;acirc;y ch&amp;ugrave;m ng&amp;acirc;y m&amp;agrave;&amp;nbsp;UV skin perfection (01 Light beige) c&amp;oacute; khả năng c&amp;acirc;n bằng dầu, mồ h&amp;ocirc;i, nhưng kh&amp;ocirc;ng&amp;nbsp;g&amp;acirc;y b&amp;iacute;t lỗ ch&amp;acirc;n l&amp;ocirc;ng.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;color:#ff0066&quot;&gt;Hướng dẫn sử dụng&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Lấy lượng vừa đủ ra tay, b&amp;ocirc;i đều l&amp;ecirc;n c&amp;aacute;c v&amp;ugrave;ng tr&amp;ecirc;n mặt. C&amp;oacute; thể b&amp;ocirc;i lại nhiều lần trong ng&amp;agrave;y.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;color:#ff0066&quot;&gt;Pro Tip&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Đặc biệt, bạn c&amp;oacute; thể x&amp;agrave;i cả hai m&amp;agrave;u để c&amp;oacute; l&amp;agrave;n da ho&amp;agrave;n hảo hơn. Lấy lượng vừa đủ m&amp;agrave;u số 1 l&amp;ecirc;n c&amp;aacute;c v&amp;ugrave;ng&amp;nbsp;như mũi, tr&amp;aacute;n, g&amp;ograve; m&amp;aacute; để hightlight. Cho c&amp;aacute;c v&amp;ugrave;ng quanh khu&amp;ocirc;n mặt c&amp;ograve;n lại c&amp;aacute;c bạn lấy số 2, rồi d&amp;ugrave;ng tay&amp;nbsp;hoặc b&amp;ocirc;ng trang điểm t&amp;aacute;n đều kem. Nếu muốn để l&amp;acirc;u tr&amp;ocirc;i hay c&amp;oacute; lớp trang điểm d&amp;agrave;y hơn, c&amp;oacute; thể phủ&amp;nbsp;th&amp;ecirc;m phấn l&amp;ecirc;n.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;color:#ff0066&quot;&gt;Th&amp;agrave;nh phần ch&amp;iacute;nh&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Tinh dầu tầm xu&amp;acirc;n, tinh dầu ph&amp;uacute;c bồn tử, dầu hạt lựu, chất kho&amp;aacute;ng.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;color:#ff0066&quot;&gt;C&amp;aacute;c th&amp;agrave;nh ph&amp;acirc;̀n kh&amp;aacute;c&amp;nbsp;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Nước &amp;eacute;p l&amp;aacute; c&amp;acirc;y l&amp;ocirc; hội, chất kho&amp;aacute;ng titan, dung dịch mềm da, diheptyl succinate,&amp;nbsp;diglycerin, dầu chuối, dầu lựu, biến thể dầu dừa, tinh chất c&amp;aacute;c loại hoa hồng, tinh dầu tầm xu&amp;acirc;n, tinh dầu&amp;nbsp;ph&amp;uacute;c bồn tử, tinh dầu vỏ cam ngọt, dầu tr&amp;aacute;i m&amp;agrave;ng tang, tinh chất cam đắng, tinh chất hoa c&amp;uacute;c la m&amp;atilde;, tinh&amp;nbsp;chất bạc h&amp;agrave;, chiết xuất tr&amp;aacute;i việt quất, tinh dầu oải hương, &amp;nbsp;Mica, Lecithin, Citric Acid, Sodium Benzoate,&amp;nbsp;Behenyl Alcohol, Glyceryl Stearate, Isostearic Acid, Polyhydroxystearic Acid, Stearic Acid, Glyceryl&amp;nbsp;Undecylenate, Xanthan Gum, Benzyl Alcohol, Capryloyl Glycerin/Sebacic Acid Copolymer, Tocopherol,&amp;nbsp;Aluminum Hydroxide, Glycine Soja Sterols, Maltodextrin, Magnesium Stearate, Phytic Acid,&amp;nbsp;Dehydroacetic Acid, 3-Hexenol,&amp;nbsp;Limonene, Farnesol, Geraniol, Citronellol, Linalool.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n', '', '', '');
INSERT INTO `oc_product_description` VALUES ('197', '2', 'Certified Organic Rosehip Oil (20ml)', '&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;Lợi &amp;iacute;ch&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Hoạt chất tự nhi&amp;ecirc;n mạnh tinh dầu khiến n&amp;oacute; c&amp;oacute; hiệu quả cao cho c&amp;aacute;c vết sẹo v&amp;agrave; vết rạn da v&amp;agrave; những lo ngại kh&amp;aacute;c như da mất nước v&amp;agrave; l&amp;atilde;o h&amp;oacute;a.&lt;br /&gt;\r\nChuy&amp;ecirc;n gia của ch&amp;uacute;ng t&amp;ocirc;i đảm bảo tinh dầu tầm xu&amp;acirc;n chất lượng tốt nhất với mức h&amp;agrave;m lượng tối thiểu thiết yếu 80% axit b&amp;eacute;o (omega 3 v&amp;agrave; 6) v&amp;agrave; axit b&amp;eacute;o (omega 9).&lt;br /&gt;\r\nC&amp;aacute;c nghi&amp;ecirc;n cứu l&amp;acirc;m s&amp;agrave;ng độc lập cho thấy chứng nhận tinh dầu tầm xu&amp;acirc;n hữu cơ gi&amp;uacute;p cải thiện sự xuất hiện của những vết sẹo, rạn da, nếp nhăn v&amp;agrave; vết nhăn.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;L&amp;agrave;m thế n&amp;agrave;o để &amp;aacute;p dụng&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Nhẹ nh&amp;agrave;ng &amp;nbsp;xoa 2-3 giọt v&amp;agrave;o mặt v&amp;agrave; / hoặc cơ thể. Sử dụng v&amp;agrave;o buổi s&amp;aacute;ng v&amp;agrave; ban đ&amp;ecirc;m hoặc khi cần thiết. Sử dụng một m&amp;igrave;nh hoặc kết hợp với c&amp;aacute;c sản phẩm giữ ẩm thường xuy&amp;ecirc;n của bạn. Kh&amp;ocirc;ng &amp;aacute;p dụng l&amp;ecirc;n da bị hỏng.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:18px&quot;&gt;&lt;span style=&quot;color:rgb(255, 0, 153)&quot;&gt;Th&amp;agrave;nh phần&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Rosa Canina (rosehip) seed oil&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Theo thời gian, những thay đổi nhỏ c&amp;oacute; thể xảy ra với danh s&amp;aacute;ch th&amp;agrave;nh phần n&amp;agrave;y. Lu&amp;ocirc;n lu&amp;ocirc;n tham khảo c&amp;aacute;c sản phẩm bao b&amp;igrave;.&lt;br /&gt;\r\nĐể xem những sản phẩm tốt nhất cho bạn, h&amp;atilde;y thử chọn Skincare của ch&amp;uacute;ng t&amp;ocirc;i.&lt;/span&gt;&lt;br /&gt;\r\n&amp;nbsp;&lt;/p&gt;\r\n', 'Hoạt chất tự nhiên mạnh tinh dầu khiến nó có hiệu quả cao cho các vết sẹo và vết rạn da và những lo ngại khác như da mất nước và lão hóa...', 'Certified Organic Rosehip Oil (20ml), Trilogy, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp', 'Certified Organic Rosehip Oil (20ml), Trilogy, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp');
INSERT INTO `oc_product_description` VALUES ('198', '4', 'Rosehip Oil Antioxidant+ (15ml)', '&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;Lợi &amp;iacute;ch&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Một tinh dầu l&amp;agrave;m đẹp hiệu quả tr&amp;ecirc;n khu&amp;ocirc;n mặt, Rosehip Oil Antioxidant + tập hợp c&amp;aacute;c th&amp;agrave;nh phần mạnh nhất của tự nhi&amp;ecirc;n trong một chai nhỏ.&lt;br /&gt;\r\nKết hợp những lợi &amp;iacute;ch trẻ h&amp;oacute;a của tầm xu&amp;acirc;n v&amp;agrave; nam việt quất, chất chống oxi h&amp;oacute;a từ c&amp;agrave; chua v&amp;agrave; acai, v&amp;agrave; chiết xuất từ yến mạch nu&amp;ocirc;i dưỡng, hỗn hợp tinh dầu hạt giống phong ph&amp;uacute; (Rosapene &amp;trade;) cung cấp dinh dưỡng chuy&amp;ecirc;n s&amp;acirc;u, bổ sung th&amp;ecirc;m sự mềm mại v&amp;agrave; cải thiện độ đ&amp;agrave;n hồi đồng thời cung cấp sự bảo vệ chống oxy h&amp;oacute;a h&amp;agrave;ng ng&amp;agrave;y cho rạng rỡ khỏe mạnh, l&amp;agrave;n da s&amp;aacute;ng .&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:18px&quot;&gt;&lt;span style=&quot;color:rgb(255, 0, 153)&quot;&gt;L&amp;agrave;m thế n&amp;agrave;o để &amp;aacute;p dụng&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Sau khi rửa mặt, xoa 2-3 giọt v&amp;agrave;o mặt, cổ v&amp;agrave; ngực. Sử dụng v&amp;agrave;o buổi s&amp;aacute;ng v&amp;agrave; / hoặc tối một m&amp;igrave;nh hoặc kết hợp với c&amp;aacute;c sản phẩm giữ ẩm thường xuy&amp;ecirc;n của bạn.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;Th&amp;agrave;nh phần&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Rosa Canina (rosehip) seed oil, Solanum lycopersicum (tomato) seed oil (and) caprylic/capric triglyceride (and) vac