INSERT INTO `oc_product_description` VALUES ('209', '2', 'Raha Natural Perfume', '&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;Lợi &amp;iacute;ch&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;C&amp;oacute; nghĩa l&amp;agrave; &amp;#39;Joy&amp;#39; trong tiếng Swahili, Raha l&amp;agrave; bước đột ph&amp;aacute; thứ hai của ch&amp;uacute;ng t&amp;ocirc;i v&amp;agrave;o thế giới của hương thơm tự nhi&amp;ecirc;n trong quan hệ với &amp;nbsp;đối t&amp;aacute;c V&amp;igrave; vậy, họ c&amp;oacute; thể, một tổ chức phi lợi nhuận, hoạt động gi&amp;aacute;o dục v&amp;agrave; trao quyền cho c&amp;aacute;c cộng đồng ở ch&amp;acirc;u Phi.&lt;br /&gt;\r\nLấy cảm hứng từ mưa Phi ấm &amp;aacute;p mang lại niềm vui, sự tăng trưởng v&amp;agrave; tỷ lệ sống, hương thơm tươi m&amp;aacute;t của Raha gợi l&amp;ecirc;n một cảm gi&amp;aacute;c sống lại mưa trong vẫn c&amp;ograve;n n&amp;oacute;ng trong ng&amp;agrave;y.&lt;br /&gt;\r\nV&amp;agrave; với sự gi&amp;uacute;p đỡ của bạn, ch&amp;uacute;ng t&amp;ocirc;i đang quy&amp;ecirc;n g&amp;oacute;p NZ $2 từ mỗi chai b&amp;aacute;n ra. V&amp;igrave; vậy, họ c&amp;oacute; thể.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp;Hương đầu: galbanum xanh tươi s&amp;ocirc;i động, cam chanh, bergamot, hạt ti&amp;ecirc;u đen&amp;nbsp;&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; &amp;nbsp;Hương giữa: iris xanh, ph&amp;ugrave; thủy, hoa hồng, hương trầm&lt;br /&gt;\r\n&amp;nbsp; &amp;nbsp; &amp;nbsp;Hương cơ bản: vani ấm &amp;aacute;p, hương vị tinh tế&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;L&amp;agrave;m thế n&amp;agrave;o để &amp;aacute;p dụng&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Cuộn v&amp;agrave;o c&amp;aacute;c điểm xung thường xuy&amp;ecirc;n như mong muốn, xoa b&amp;oacute;p trong khi cần thiết. Cho ph&amp;eacute;p để kh&amp;ocirc; ho&amp;agrave;n to&amp;agrave;n trước khi cho ph&amp;eacute;p tiếp x&amp;uacute;c với quần &amp;aacute;o.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:18px&quot;&gt;&lt;span style=&quot;color:rgb(255, 0, 153)&quot;&gt;Th&amp;agrave;nh phần&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Helianthus Annuus (Sunflower) Seed Oil , Caprylic/capric triglyceride, Parfum, Simmondsia Chinensis (Jojoba) Seed Oil, Tocopheryl acetate, Citral, Citronellol, Geraniol, Limonene, Linalool&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Theo thời gian, những thay đổi nhỏ c&amp;oacute; thể xảy ra với danh s&amp;aacute;ch th&amp;agrave;nh phần n&amp;agrave;y. Lu&amp;ocirc;n lu&amp;ocirc;n tham khảo c&amp;aacute;c sản phẩm bao b&amp;igrave;.&lt;br /&gt;\r\nĐể xem những sản phẩm tốt nhất cho bạn, h&amp;atilde;y thử chọn Skincare của ch&amp;uacute;ng t&amp;ocirc;i.&lt;/span&gt;&lt;br /&gt;\r\n&amp;nbsp;&lt;/p&gt;\r\n', 'Có nghĩa là \'Joy\' trong tiếng Swahili, Raha là bước đột phá thứ hai của chúng tôi vào thế giới của hương thơm tự nhiên trong quan hệ với  đối tác Vì vậy, họ có thể, một tổ chức phi lợi nhuận, hoạt động giáo dục và trao quyền cho các cộng đồng ở châu Phi..', 'Raha Natural Perfume, Trilogy, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp', 'Raha Natural Perfume, Trilogy, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp');
INSERT INTO `oc_product_description` VALUES ('208', '2', 'Botanical Body Wash', '&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;Lợi &amp;iacute;ch&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Rửa cơ thể với một dạng &amp;nbsp;gel nhẹ nh&amp;agrave;ng cung cấp l&amp;agrave;n da mới đặc biệt, sạch sẽ. H&amp;atilde;y tận hưởng, thư gi&amp;atilde;n, m&amp;ugrave;i hương thơm trị liệu mạnh mẽ trong khi chất tẩy rửa kh&amp;ocirc;ng kh&amp;ocirc; nhẹ nh&amp;agrave;ng l&amp;agrave;m mới l&amp;agrave;n da để bạn sẵn s&amp;agrave;ng h&amp;agrave;nh động.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;L&amp;agrave;m thế n&amp;agrave;o để &amp;aacute;p dụng&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Bơm một lượng nhỏ v&amp;agrave;o tay hoặc khăn lau, thoa l&amp;ecirc;n cơ thể, rửa v&amp;agrave; rửa sạch. Th&amp;ecirc;m v&amp;agrave;o hoạt động bồn nước để tắm hương thơm trị liệu thư gi&amp;atilde;n.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;Th&amp;agrave;nh phần&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Aqua (water), Lauryl glucoside, Sodium coco-sulfate, Aloe barbadenis leaf juice, Macropiper excelsum leaf extract, Phyllanthus emblica officinalis fruit extract, Arctium lappa root extract, Chamomilla recutita flower extract, Rosa Canina (rosehip) seed oil, Simmondsia Chinensis (Jojoba) Seed Oil, Cucumis sativa fruit extract, Citric acid, Dehydroacetic acid (and) benzyl alcohol, Parfum, Geraniol, Citronellol, Linalool&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Theo thời gian, những thay đổi nhỏ c&amp;oacute; thể xảy ra với danh s&amp;aacute;ch th&amp;agrave;nh phần n&amp;agrave;y. Lu&amp;ocirc;n lu&amp;ocirc;n tham khảo c&amp;aacute;c sản phẩm bao b&amp;igrave;.&lt;br /&gt;\r\nĐể xem những sản phẩm tốt nhất cho bạn, h&amp;atilde;y thử chọn Skincare của ch&amp;uacute;ng t&amp;ocirc;i.&lt;/span&gt;&lt;br /&gt;\r\n&amp;nbsp;&lt;/p&gt;\r\n', 'Rửa cơ thể với một dạng  gel nhẹ nhàng cung cấp làn da mới đặc biệt, sạch sẽ. Hãy tận hưởng, thư giãn, mùi hương thơm trị liệu mạnh mẽ trong khi chất tẩy rửa không khô nhẹ nhàng làm mới làn da để bạn sẵn sàng hành động...', 'Botanical Body Wash, Trilogy, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp', 'Botanical Body Wash, Trilogy, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp');
