INSERT INTO `oc_product_description` VALUES ('204', '2', 'Ultra Hydrating Hand Cream', '&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;Lợi &amp;iacute;ch&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Kem b&amp;ocirc;i da tay mạnh mẽ nhất của ch&amp;uacute;ng t&amp;ocirc;i cung cấp si&amp;ecirc;u giữ ẩm với một m&amp;agrave;u cam ngọt ng&amp;agrave;o, hổ ph&amp;aacute;ch v&amp;agrave; vani hương thơm tinh tế. Sử dụng h&amp;agrave;ng ng&amp;agrave;y mang lại b&amp;agrave;n tay mềm mại si&amp;ecirc;u mịn với một l&amp;aacute; chắn chống lại mất m&amp;aacute;t độ ẩm.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;L&amp;agrave;m thế n&amp;agrave;o để &amp;aacute;p dụng&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Xoa v&amp;agrave;o tay, tập trung v&amp;agrave;o c&amp;aacute;c khu vực kh&amp;ocirc;. Sử dụng h&amp;agrave;ng ng&amp;agrave;y hoặc thường xuy&amp;ecirc;n theo y&amp;ecirc;u cầu.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;Th&amp;agrave;nh phần&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Aqua (water), Aloe barbadenis leaf juice, Glyceryl stearate, Cetearyl alcohol, Caprylic/capric triglyceride, Glycerin (vegetable), Honey (mel), Rosa Canina (rosehip) seed oil, Cocos nucifera oil, Sclerocarya birrea seed oil, Camellia Sinensis (White Tea) Extract, Avena sativa kernel extract, Chamomilla recutita flower extract, Calendula officinalis flower extract (and) glycine soja (soybean) oil, Sodium stearoyl glutamate, Citric acid, Dehydroacetic acid (and) benzyl alcohol, Parfum, Citral, Farnesol, Citronellol, Linalool, Geraniol, Limonene&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Theo thời gian, những thay đổi nhỏ c&amp;oacute; thể xảy ra với danh s&amp;aacute;ch th&amp;agrave;nh phần n&amp;agrave;y. Lu&amp;ocirc;n lu&amp;ocirc;n tham khảo c&amp;aacute;c sản phẩm bao b&amp;igrave;.&lt;br /&gt;\r\nĐể xem những sản phẩm tốt nhất cho bạn, h&amp;atilde;y thử chọn Skincare của ch&amp;uacute;ng t&amp;ocirc;i.&lt;/span&gt;&lt;br /&gt;\r\n&amp;nbsp;&lt;/p&gt;\r\n', 'Kem bôi da tay mạnh mẽ nhất của chúng tôi cung cấp siêu giữ ẩm với một màu cam ngọt ngào, hổ phách và vani hương thơm tinh tế. Sử dụng hàng ngày mang lại bàn tay mềm mại siêu mịn với một lá chắn chống lại mất mát độ ẩm...', 'Ultra Hydrating Hand Cream, Trilogy, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp', 'Ultra Hydrating Hand Cream, Trilogy, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp');
INSERT INTO `oc_product_description` VALUES ('203', '2', 'Rose Hand Cream', '&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;Lợi &amp;iacute;ch&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;C&amp;aacute;ch điều trị ho&amp;agrave;n hảo cung cấp sự mềm mại l&amp;acirc;u d&amp;agrave;i, xoa dịu biểu b&amp;igrave; t&amp;oacute;c v&amp;agrave; tăng cường m&amp;oacute;ng tay. Thưởng thức hương thơm hoa hồng ngọt ng&amp;agrave;o mềm mại như b&amp;agrave;n tay của bạn được hưởng lợi từ kem dầu dưỡng t&amp;oacute;c gọn nhẹ của ch&amp;uacute;ng t&amp;ocirc;i.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:18px&quot;&gt;&lt;span style=&quot;color:rgb(255, 0, 153)&quot;&gt;L&amp;agrave;m thế n&amp;agrave;o để sử dụng&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Xoa v&amp;agrave;o tay v&amp;agrave; lớp biểu b&amp;igrave;. Sử dụng h&amp;agrave;ng ng&amp;agrave;y hoặc thường xuy&amp;ecirc;n theo y&amp;ecirc;u cầu.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;Th&amp;agrave;nh phần&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Aqua (water), Glycerin (vegetable), Glyceryl stearate, Prunus amygdalus dulcis oil, Oenothera biennis seed oil, Stearic acid, Rosa Canina (rosehip) seed oil, Simmondsia Chinensis (Jojoba) Seed Oil, Sodium stearoyl glutamate, Theobroma cacao seed butter, Butyrospermum parkii, Aloe barbadenis leaf extract, Tocopheryl acetate, Althaea officinalis root extract, Equisetum arvense leaf extract, Calendula officinalis flower extract (and) glycine soja (soybean) oil, Persea gratissima, Parfum, Citric acid, Dehydroacetic acid (and) benzyl alcohol, Citral, Geraniol, Citronellol, Linalool&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Theo thời gian, những thay đổi nhỏ c&amp;oacute; thể xảy ra với danh s&amp;aacute;ch th&amp;agrave;nh phần n&amp;agrave;y. Lu&amp;ocirc;n lu&amp;ocirc;n tham khảo c&amp;aacute;c sản phẩm bao b&amp;igrave;.&lt;br /&gt;\r\nĐể xem những sản phẩm tốt nhất cho bạn, h&amp;atilde;y thử chọn Skincare của ch&amp;uacute;ng t&amp;ocirc;i.&lt;/span&gt;&lt;br /&gt;\r\n&amp;nbsp;&lt;/p&gt;\r\n', 'Cách điều trị hoàn hảo cung cấp sự mềm mại lâu dài, xoa dịu biểu bì tóc và tăng cường móng tay. Thưởng thức hương thơm hoa hồng ngọt ngào mềm mại như bàn tay của bạn được hưởng lợi từ kem dầu dưỡng tóc gọn nhẹ của chúng tôi...', 'Rose Hand Cream, Trilogy, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp', 'Rose Hand Cream, Trilogy, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp');
INSERT INTO `oc_product_description` VALUES ('202', '2', 'Instant Bronzing Gel (100ml)', '&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;Lợi &amp;iacute;ch&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Thưởng thức an to&amp;agrave;n, ho&amp;agrave;n to&amp;agrave;n tự nhi&amp;ecirc;n v&amp;agrave; dễ d&amp;agrave;ng để &amp;aacute;p dụng bronze s&amp;aacute;ng với dạng gel thuộc da kh&amp;ocirc; nhanh n&amp;agrave;y. Mang lại m&amp;agrave;u da tức th&amp;igrave; về ứng dụng n&amp;acirc;ng cao với &amp;aacute;nh s&amp;aacute;ng phản chiếu mica kho&amp;aacute;ng. Sử dụng một lần cho một &amp;aacute;nh s&amp;aacute;ng m&amp;agrave;u v&amp;agrave;ng ngay lập tức hoặc h&amp;agrave;ng ng&amp;agrave;y phải x&amp;acirc;y dựng một bronze r&amp;aacute;m nắng k&amp;eacute;o d&amp;agrave;i 2-3 ng&amp;agrave;y.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;L&amp;agrave;m thế n&amp;agrave;o để &amp;aacute;p dụng&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Cho c&amp;aacute;c kết quả tốt nhất &amp;aacute;p dụng sau khi tẩy tế b&amp;agrave;o da. Cẩn thận pha trộn trong c&amp;aacute;c khu vực bạn mong muốn tan v&amp;agrave; để kh&amp;ocirc; ho&amp;agrave;n to&amp;agrave;n. Rửa tay ngay sau khi sử dụng để tr&amp;aacute;nh vết bẩn. Xin lưu &amp;yacute;: kh&amp;ocirc;ng cung cấp bảo vệ UV.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;Th&amp;agrave;nh phần&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Aqua (water), Limonene, Sodium hydroxide, Chlorophyllin-Copper Complex CI75810, Citric acid, Aloe barbadenis leaf extract, Mica &amp;amp; Titanium Oxide &amp;amp; Iron Oxides CI77019, CI77891, CI77489, Caramel, DHA, Alcohol&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Theo thời gian, những thay đổi nhỏ c&amp;oacute; thể xảy ra với danh s&amp;aacute;ch th&amp;agrave;nh phần n&amp;agrave;y. Lu&amp;ocirc;n lu&amp;ocirc;n tham khảo c&amp;aacute;c sản phẩm bao b&amp;igrave;.&lt;br /&gt;\r\nĐể xem những sản phẩm tốt nhất cho bạn, h&amp;atilde;y thử chọn Skincare của ch&amp;uacute;ng t&amp;ocirc;i.&lt;/span&gt;&lt;br /&gt;\r\n&amp;nbsp;&lt;/p&gt;\r\n', 'Thưởng thức an toàn, hoàn toàn tự nhiên và dễ dàng để áp dụng bronze sáng với dạng gel thuộc da khô nhanh này. Mang lại màu da tức thì về ứng dụng nâng cao với ánh sáng phản chiếu mica khoáng...', 'Instant Bronzing Gel (100ml), Trilogy, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp', 'Instant Bronzing Gel (100ml), Trilogy, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp');
INSERT INTO `oc_product_description` VALUES ('201', '4', 'Eye Contour Cream', '&lt;p&gt;&lt;span style=&quot;font-size:18px&quot;&gt;&lt;span style=&quot;color:rgb(255, 0, 153)&quot;&gt;Benefits&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Restore&amp;nbsp;a smoother, firmer appearance to the delicate eye contour area with this unique gel/cream formulation. Powerful anti-ageing care for morning and night; it&amp;#39;s also perfect anytime for tired, travel-weary eyes. Fast absorbing for immediate make-up application.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;How to apply&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Apply sparingly to cleansed skin before moisturising. Gently pat from the outer eye towards the nose using fingertips.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;Ingredients&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Aqua (water), Prunus amygdalus dulcis oil, Glycerin (vegetable), Oenothera biennis seed oil, Simmondsia Chinensis (Jojoba) Seed Oil, Glyceryl stearate, Stearic acid, Cetearyl alcohol, Rosa Canina (Rosehip) Seed Oil, Solanum Lycopersicum (Tomato) Seed Oil (and) Caprylic/Capric Triglyceride (and) Vaccinium Macrocarpon (Cranberry) Seed Oil (and) Helianthus Annuus Seed Oil (and) Tocopherol, Lycopene, Euterpe Oleracea (Acai) Fruit Oil (and) Tocopherol, Avena Sativa (Oat) Kernel Extract , Butyrospermum parkii, Theobroma cacao seed butter, Aloe barbadenis leaf extract, Persea gratissima, Euphrasia officinalis extract, Tocopheryl acetate, Daucus carota sativa seed oil, Sodium stearoyl glutamate, Citric acid, Dehydroacetic acid (and) benzyl alcohol&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;em&gt;From time to time minor changes may occur to this ingredient list. Always refer to the product packaging.&lt;/em&gt;&lt;/span&gt;&lt;/p&gt;\r\n', 'Restore a smoother, firmer appearance to the delicate eye contour area with this unique gel/cream formulation. Powerful anti-ageing care for morning and night; it\'s also perfect anytime for tired, travel-weary eyes. Fast absorbing for immediate make-up ap', 'Eye Contour Cream, Trilogy, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp', 'Eye Contour Cream, Trilogy, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp');
INSERT INTO `oc_product_description` VALUES ('200', '4', 'Rosehip Oil Lip Balm', '&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;Benefits&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Condition for an instant reprieve from dryness with our ultra-moisturising creamy lip balm. Daily use will help promote soft, smooth, perfectly kissable lips protected from the damaging effects of the environment.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;How to apply&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Apply directly to lips anytime, all the time.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;Ingradients&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Ricinus communis seed oil, Cera alba, Rosa Canina (rosehip) seed oil, Simmondsia Chinensis (Jojoba) Seed Oil, Daucus carota sativa seed oil, Theobroma cacao seed butter, Butyrospermum parkii, Euterpe oleracea fruit oil, Menta piperita oil, Cinnamal, Limonene, Linalool&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;em&gt;From time to time minor changes may occur to this ingredient list. Always refer to the product packaging.&lt;/em&gt;&lt;/span&gt;&lt;/p&gt;\r\n', 'Condition for an instant reprieve from dryness with our ultra-moisturising creamy lip balm. Daily use will help promote soft, smooth, perfectly kissable lips protected from the damaging effects of the environment...', 'Rosehip Oil Lip Balm, Trilogy, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp', 'Rosehip Oil Lip Balm, Trilogy, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp');
INSERT INTO `oc_product_description` VALUES ('199', '4', 'Everything Balm (45ml)', '&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;Benefits&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Everything Balm fixes everything &amp;hellip; use me everywhere &amp;hellip; nourishing and protecting for face and body &amp;hellip; soothing dry, chapped skin &amp;hellip; softening, smoothing and moisturising &amp;hellip; lip conditioning &amp;hellip; cuticle care &amp;hellip; melting massage oil &amp;hellip; ideal for the whole family (bubs as well) &amp;hellip; perfect for travel&amp;hellip;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;How to apply&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Warm in hands or fingertips. Melts on contact with skin and absorbs rapidly. Use anywhere, anytime.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;Ingredients&lt;/span&gt;&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:16px&quot;&gt;Prunus amygdalus dulcis oil, Cera alba, Carica papaya (papaya) fruit extract (and) glycine soja (soybean) oil, Sclerocarya birrea seed oil, Oenothera biennis seed oil, Rosa Canina (rosehip) seed oil, Triticum vulgare germ oil, Calendula officinalis flower extract (and) glycine soja (soybean) oil, Echinacea purpurea(coneflower) extract (and) glycine soja (soybean) oil, Cocos nucifera oil, Tocopheryl acetate, Simmondsia Chinensis (Jojoba) Seed Oil, Olea europaea fruit oil, Honey (mel), Parfum, Citral, Geraniol, Citronellol, Limonene, Linalool&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;em&gt;From time to time minor changes may occur to this ingredient list. Always refer to the product packaging.&lt;/em&gt;&lt;/span&gt;&lt;/p&gt;\r\n', 'Mọi thứ dầu thơm chăm sóc khắc phục tất cả mọi thứ ... tôi sử dụng khắp mọi nơi ... nuôi dưỡng và bảo vệ cho mặt và cơ thể ... làm dịu da khô, nứt nẻ ... làm mềm, làm dịu và giữ ẩm ... điều hòa môi ... chăm sóc lớp biểu bì ... tinh dầu mát xa tan chảy...', 'Everything Balm (45ml), Trilogy, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp', 'Everything Balm (45ml), Trilogy, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp');
INSERT INTO `oc_product_description` VALUES ('198', '2', 'Rosehip Oil Antioxidant+ (15ml)', '&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;Lợi &amp;iacute;ch&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Một tinh dầu l&amp;agrave;m đẹp hiệu quả tr&amp;ecirc;n khu&amp;ocirc;n mặt, Rosehip Oil Antioxidant + tập hợp c&amp;aacute;c th&amp;agrave;nh phần mạnh nhất của tự nhi&amp;ecirc;n trong một chai nhỏ.&lt;br /&gt;\r\nKết hợp những lợi &amp;iacute;ch trẻ h&amp;oacute;a của tầm xu&amp;acirc;n v&amp;agrave; nam việt quất, chất chống oxi h&amp;oacute;a từ c&amp;agrave; chua v&amp;agrave; acai, v&amp;agrave; chiết xuất từ yến mạch nu&amp;ocirc;i dưỡng, hỗn hợp tinh dầu hạt giống phong ph&amp;uacute; (Rosapene &amp;trade;) cung cấp dinh dưỡng chuy&amp;ecirc;n s&amp;acirc;u, bổ sung th&amp;ecirc;m sự mềm mại v&amp;agrave; cải thiện độ đ&amp;agrave;n hồi đồng thời cung cấp sự bảo vệ chống oxy h&amp;oacute;a h&amp;agrave;ng ng&amp;agrave;y cho rạng rỡ khỏe mạnh, l&amp;agrave;n da s&amp;aacute;ng .&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:18px&quot;&gt;&lt;span style=&quot;color:rgb(255, 0, 153)&quot;&gt;L&amp;agrave;m thế n&amp;agrave;o để &amp;aacute;p dụng&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Sau khi rửa mặt, xoa 2-3 giọt v&amp;agrave;o mặt, cổ v&amp;agrave; ngực. Sử dụng v&amp;agrave;o buổi s&amp;aacute;ng v&amp;agrave; / hoặc tối một m&amp;igrave;nh hoặc kết hợp với c&amp;aacute;c sản phẩm giữ ẩm thường xuy&amp;ecirc;n của bạn.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;Th&amp;agrave;nh phần&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Rosa Canina (rosehip) seed oil, Solanum lycopersicum (tomato) seed oil (and) caprylic/capric triglyceride (and) vaccinium macrocarpon (cranberry) seed oil (and) helianthus annuus seed oil (and) tocopherol, Lycopene, Euterpe oleracea fruit oil, Avena sativa kernel extract&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Theo thời gian, những thay đổi nhỏ c&amp;oacute; thể xảy ra với danh s&amp;aacute;ch th&amp;agrave;nh phần n&amp;agrave;y. Lu&amp;ocirc;n lu&amp;ocirc;n tham khảo c&amp;aacute;c sản phẩm bao b&amp;igrave;.&lt;br /&gt;\r\nĐể xem những sản phẩm tốt nhất cho bạn, h&amp;atilde;y thử chọn Skincare của ch&amp;uacute;ng t&amp;ocirc;i.&lt;/span&gt;&lt;br /&gt;\r\n&amp;nbsp;&lt;/p&gt;\r\n', 'Một tinh dầu làm đẹp hiệu quả trên khuôn mặt, Rosehip Oil Antioxidant + tập hợp các thành phần mạnh nhất của tự nhiên trong một chai nhỏ...', 'Rosehip Oil Antioxidant+ (15ml), Trilogy, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp', 'Rosehip Oil Antioxidant+ (15ml), Trilogy, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp');
INSERT INTO `oc_product_description` VALUES ('196', '2', 'Certified Organic Rosehip Oil (45ml)', '&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;Lợi &amp;iacute;ch&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Hoạt chất tự nhi&amp;ecirc;n mạnh tinh dầu khiến n&amp;oacute; c&amp;oacute; hiệu quả cao cho c&amp;aacute;c vết sẹo v&amp;agrave; vết rạn da v&amp;agrave; những lo ngại kh&amp;aacute;c như da mất nước v&amp;agrave; l&amp;atilde;o h&amp;oacute;a.&lt;br /&gt;\r\nChuy&amp;ecirc;n gia của ch&amp;uacute;ng t&amp;ocirc;i đảm bảo tinh dầu tầm xu&amp;acirc;n chất lượng tốt nhất với mức h&amp;agrave;m lượng tối thiểu thiết yếu 80% axit b&amp;eacute;o (omega 3 v&amp;agrave; 6) v&amp;agrave; axit b&amp;eacute;o (omega 9).&lt;br /&gt;\r\nC&amp;aacute;c nghi&amp;ecirc;n cứu l&amp;acirc;m s&amp;agrave;ng độc lập cho thấy chứng nhận tinh dầu tầm xu&amp;acirc;n hữu cơ gi&amp;uacute;p cải thiện sự xuất hiện của những vết sẹo, rạn da, nếp nhăn v&amp;agrave; vết nhăn.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;L&amp;agrave;m thế n&amp;agrave;o để &amp;aacute;p dụng&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Nhẹ nh&amp;agrave;ng &amp;nbsp;xoa 2-3 giọt v&amp;agrave;o mặt v&amp;agrave; / hoặc cơ thể. Sử dụng v&amp;agrave;o buổi s&amp;aacute;ng v&amp;agrave; ban đ&amp;ecirc;m hoặc khi cần thiết. Sử dụng một m&amp;igrave;nh hoặc kết hợp với c&amp;aacute;c sản phẩm giữ ẩm thường xuy&amp;ecirc;n của bạn. Kh&amp;ocirc;ng &amp;aacute;p dụng l&amp;ecirc;n da bị hỏng.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;Th&amp;agrave;nh phần&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Rosa Canina (rosehip) seed oil&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Theo thời gian, những thay đổi nhỏ c&amp;oacute; thể xảy ra với danh s&amp;aacute;ch th&amp;agrave;nh phần n&amp;agrave;y. Lu&amp;ocirc;n lu&amp;ocirc;n tham khảo c&amp;aacute;c sản phẩm bao b&amp;igrave;.&lt;br /&gt;\r\nĐể xem những sản phẩm tốt nhất cho bạn, h&amp;atilde;y thử chọn Skincare của ch&amp;uacute;ng t&amp;ocirc;i.&lt;/span&gt;&lt;br /&gt;\r\n&amp;nbsp;&lt;/p&gt;\r\n', 'Hoạt chất tự nhiên mạnh tinh dầu khiến nó có hiệu quả cao cho các vết sẹo và vết rạn da và những lo ngại khác như da mất nước và lão hóa...', 'Certified Organic Rosehip Oil (45ml), Trilogy, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp', 'Certified Organic Rosehip Oil (45ml), Trilogy, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp');
INSERT INTO `oc_product_description` VALUES ('197', '4', 'Certified Organic Rosehip Oil (20ml)', '&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;Benefits&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Rosehip oil&amp;#39;s potent natural actives make it highly effective for scars and stretch marks and other skin concerns including dehydrated and ageing skin. &amp;nbsp;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Our rosehip expertise ensures the finest quality oil with minimum 80% essential fatty acid (omega 3 and 6) and fatty acid (omega 9) content.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Independent clinical studies show Certified Organic Rosehip Oil helps improve the appearance of scars, stretchmarks, fine lines and wrinkles.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;How to apply&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Gently massage 2-3 drops into face and/or body. Use morning and night or as required. Use alone or in conjunction with your regular moisturising products. Do not apply to broken skin.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:18px&quot;&gt;&lt;span style=&quot;color:rgb(255, 0, 153)&quot;&gt;Ingredients&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Rosa Canina (rosehip) seed oil&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;em&gt;From time to time minor changes may occur to this ingredient list. Always refer to the product packaging.&lt;/em&gt;&lt;/span&gt;&lt;/p&gt;\r\n', 'Rosehip oil\'s potent natural actives make it highly effective for scars and stretch marks and other skin concerns including dehydrated and ageing skin...', 'Certified Organic Rosehip Oil (20ml), Trilogy, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp', 'Certified Organic Rosehip Oil (20ml), Trilogy, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp');
INSERT INTO `oc_product_description` VALUES ('195', '4', 'Rosehip Oil Antioxidant+', '&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;Benefits&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Our unique formulation Rosapene&amp;trade; provides intense hydration plus daily antioxidant protection.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Combining nourishing essential fatty acids with super antioxidants, lycopene and phytosterol, this certified organic beauty oil is deeply moisturising while helping to protect against environmental free radical damage.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Independent clinical studies show Rosehip Oil Antioxidant+ improves brightening, skin tone, fine lines and wrinkles, elasticity and firmness.&amp;nbsp;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;How to apply&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;After cleansing, massage 2-3 drops into face, neck and d&amp;eacute;colletage. Use morning and/or night alone or in conjunction with your regular moisturising products.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;Ingredients&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Rosa Canina (rosehip) seed oil, Solanum lycopersicum (tomato) seed oil (and) caprylic/capric triglyceride (and) vaccinium macrocarpon (cranberry) seed oil (and) helianthus annuus seed oil (and) tocopherol, Lycopene, Euterpe oleracea fruit oil, Avena sativa kernel extract&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;em&gt;From time to time minor changes may occur to this ingredient list. Always refer to the product packaging.&lt;/em&gt;&lt;/span&gt;&lt;/p&gt;\r\n', 'Our unique formulation Rosapene™ provides intense hydration plus daily antioxidant protection...', 'Rosehip Oil Antioxidant+, Trilogy, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp', 'Rosehip Oil Antioxidant+, Trilogy, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp');
INSERT INTO `oc_product_description` VALUES ('254', '2', 'THREE RHYTHM BEAUTY GETTOU &amp; SEED OIL', '&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Nếu bạn đang t&amp;igrave;m kiếm loại thực phẩm chức năng vừa gi&amp;uacute;p bạn giảm được c&amp;aacute;c tương t&amp;aacute;c với tia&amp;nbsp;UV, vừa gi&amp;uacute;p bạn bồi bổ cơ thể th&amp;igrave; Gettou and Seed Oil l&amp;agrave; sản phẩm ho&amp;agrave;n to&amp;agrave;n tuyệt vời. Tinh&amp;nbsp;chất từ rượu vang đỏ, c&amp;acirc;y riềng ấm, hạt lanh v&amp;agrave; tr&amp;aacute;i sachainchi, bạn sẽ ho&amp;agrave;n to&amp;agrave;n kh&amp;ocirc;ng phải lo&amp;nbsp;lắng về c&amp;aacute;c quan ngại của cơ thể cũng như l&amp;agrave;n da của m&amp;igrave;nh nữa.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Đặc biệt, &amp;ldquo;Riềng ấm (Alpinia Zerumbet)&amp;rdquo; l&amp;agrave; loại c&amp;acirc;y đặc biệt chỉ mọc ở những v&amp;ugrave;ng n&amp;oacute;ng ẩm,&amp;nbsp;ri&amp;ecirc;ng ở Nhật Bản l&amp;agrave; đảo Okinawa. Từ rất l&amp;acirc;u, người d&amp;acirc;n Okinawa vẫn lu&amp;ocirc;n canh t&amp;aacute;c, thu hoạch&amp;nbsp;riềng ấm để chế biến m&amp;oacute;n ăn, nước uống, b&amp;aacute;nh kẹo cũng như th&amp;ecirc;m vị tr&amp;agrave;..v.v.. Một phần v&amp;igrave; vị&amp;nbsp;cay nồng xen lẫn ngọt của n&amp;oacute;, phần v&amp;igrave; sự dinh dưỡng dồi d&amp;agrave;o c&amp;oacute; trong c&amp;acirc;y riềng ấm. Ph&amp;aacute;t hiện&amp;nbsp;được điều đ&amp;oacute;, Three đ&amp;atilde; nghi&amp;ecirc;n cứu, thu hoạch c&amp;acirc;y riềng ấm tươi về sấy kh&amp;ocirc;, say nhuyễn th&amp;agrave;nh&amp;nbsp;bột rồi đưa v&amp;agrave;o &lt;strong&gt;Gettou and Seed Oil&lt;/strong&gt; v&amp;agrave; n&amp;acirc;ng tầm vi&amp;ecirc;n thuốc bổ n&amp;agrave;y l&amp;ecirc;n một tầm cao mới.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;color:#ff0066&quot;&gt;C&amp;ocirc;ng dụng&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Thực phẩm chức năng gi&amp;uacute;p cải thiện l&amp;agrave;n da từ b&amp;ecirc;n trong. Với c&amp;aacute;c tinh chất hữu cơ gi&amp;uacute;p da&amp;nbsp;khỏe mạnh&amp;nbsp;v&amp;agrave; &amp;iacute;t bị tổn thương bởi&amp;nbsp;tia UV.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Ngo&amp;agrave;i ra, chất nhờn v&amp;agrave; đường trong c&amp;aacute;c loại thực vật c&amp;ograve;n gi&amp;uacute;p cơ thể dẻo dai, xương khớp cứng rắn. Kh&amp;ocirc;i phục trạng th&amp;aacute;i mạnh khỏe cho người ở độ tuổi trung ni&amp;ecirc;n.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;color:#ff0066&quot;&gt;Hướng dẫn sử dụng&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Một ng&amp;agrave;y uống 3 vi&amp;ecirc;n&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Khi uống kh&amp;ocirc;ng cắn kh&amp;ocirc;ng nhai, nuốt thẳng với nước.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Lưu &amp;yacute; đ&amp;oacute;ng chặt nắp khi kh&amp;ocirc;ng sử dụng&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Kh&amp;ocirc;ng để ở ph&amp;ograve;ng c&amp;oacute; nhiệt độ qu&amp;aacute; cao hay nơi tiếp x&amp;uacute;c trực tiếp với &amp;aacute;nh mặt trời.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;color:#ff0066&quot;&gt;Th&amp;agrave;nh ph&amp;acirc;̀n&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Dầu hạt lanh, tinh chất gettou, tinh bột, chất nhờn, tinh chất sữa, tinh chất tr&amp;aacute;i Sachainchi.&amp;nbsp;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Dinh dưỡng trong mỗi vi&amp;ecirc;n (370mg)&lt;br /&gt;\r\nEnergy: 2.26kcal&lt;br /&gt;\r\nProtein: 0.0036g&lt;br /&gt;\r\nDầu: 0.18g&lt;br /&gt;\r\nBột Gluxit: 0.14g&lt;br /&gt;\r\nNatri: 1.28g&lt;br /&gt;\r\nGettou: 30mg&lt;/span&gt;&lt;br /&gt;\r\n&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n', '', '', '');
INSERT INTO `oc_product_description` VALUES ('251', '2', 'Liquid foundation organic extract (Ochre 10)', '&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Với chiết xuất gạo v&amp;agrave; kho&amp;aacute;ng chất ho&amp;agrave;n to&amp;agrave;n tự nhi&amp;ecirc;n, Liquid Foundation l&amp;agrave; cứu c&amp;aacute;nh cho l&amp;agrave;n da&amp;nbsp;v&amp;agrave;o m&amp;ugrave;a h&amp;egrave; của ph&amp;aacute;i nữ. Bạn hay đổ mồ h&amp;ocirc;i, mau tr&amp;ocirc;i lớp trang điểm hay bị gợn phấn v&amp;agrave;o m&amp;ugrave;a&amp;nbsp;h&amp;egrave;? Vậy h&amp;atilde;y thay đổi ngay sản phẩm trang điểm v&amp;agrave; t&amp;igrave;m tới sản phẩm thi&amp;ecirc;n nhi&amp;ecirc;n kh&amp;ocirc;ng g&amp;acirc;y hại&amp;nbsp;da Liquid Foundation của Natural House, được sản xuất v&amp;agrave; thu gom nguy&amp;ecirc;n liệu 100% tại Nhật&amp;nbsp;Bản.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;strong&gt;Liquid foundation organic extract&amp;nbsp;m&amp;agrave;u Ochre 10 - ng&amp;atilde; v&amp;agrave;ng lạnh &amp;ndash; che khuyết điểm mụn, n&amp;aacute;m, m&amp;agrave;u tự nhi&amp;ecirc;n tr&amp;ugrave;ng m&amp;agrave;u da&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;color:#ff0066&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;C&amp;ocirc;ng dụng&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Che phủ lỗ ch&amp;acirc;n l&amp;ocirc;ng nhưng kh&amp;ocirc;ng g&amp;acirc;y sần da hay gợn phấn nhờ chất nước lidquid mịn m&amp;agrave;ng.&amp;nbsp;Thay thế cho Powder Foundation dạng phấn v&amp;agrave;o m&amp;ugrave;a h&amp;egrave; rất dễ tr&amp;ocirc;i, hay nặng mặt th&amp;igrave; dạng Liquid đem lại cảm gi&amp;aacute;c tự nhi&amp;ecirc;n như kh&amp;ocirc;ng trang điểm nhưng lại l&amp;acirc;u tr&amp;ocirc;i.&amp;nbsp;Với SPF 22 PA++, Liquid Foundation vừa c&amp;oacute; chức năng l&amp;agrave;m lớp nền trang điểm cơ bản, vừa tạo th&amp;agrave;nh một lớp m&amp;agrave;ng chống tia UV vững chắc.&amp;nbsp;Nhờ v&amp;agrave;o c&amp;aacute;c th&amp;agrave;nh phần tự nhi&amp;ecirc;n, Liquid Foundation hầu như kh&amp;ocirc;ng g&amp;acirc;y k&amp;iacute;ch ứng, dị ứng da.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;color:#ff0066&quot;&gt;Hướng dẫn sử dụng&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Lắc nhẹ, đều trước khi sử dụng. Lấy một lượng vừa đủ ra đầu ng&amp;oacute;n tay hoặc b&amp;ocirc;ng trang điểm rồi xoa đều l&amp;ecirc;n mặt.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Kh&amp;ocirc;ng để trong ph&amp;ograve;ng c&amp;oacute; nhiệt độ qu&amp;aacute; cao hoặc hơi tiếp x&amp;uacute;c trực tiếp với &amp;aacute;nh nắng mặt trời.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;color:#ff0066&quot;&gt;Th&amp;agrave;nh ph&amp;acirc;̀n&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Chiết xuất tinh chất gạo được sản xuất trong nước Nhật&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Bột chất kho&amp;aacute;ng thi&amp;ecirc;n nhi&amp;ecirc;n&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Cam kết kh&amp;ocirc;ng sử dụng h&amp;oacute;a chất bảo quản cũng như chất tạo m&amp;agrave;u, m&amp;ugrave;i.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;.&lt;/span&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;strong&gt;Hạn sử dụng&amp;nbsp;3 năm trước khi mở nắp. 6 th&amp;aacute;ng sau khi mở nắp.&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;\r\n', '', '', '');
