INSERT INTO `oc_product_description` VALUES ('191', '2', 'Very Gentle Calming Serum', '&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;Lợi &amp;iacute;ch&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;L&amp;agrave;m dịu c&amp;aacute;c r&amp;agrave;o cản l&amp;agrave;n da bị suy yếu v&amp;agrave; l&amp;agrave;m dịu mong manh, l&amp;agrave;n da bị k&amp;iacute;ch th&amp;iacute;ch hoặc kh&amp;ocirc;ng dung nạp với huyết thanh cứu nguy cực kỳ nhẹ nh&amp;agrave;ng n&amp;agrave;y. Cung cấp &amp;aacute;nh s&amp;aacute;ng v&amp;agrave; giữ ẩm hiệu quả, n&amp;oacute; kh&amp;ocirc;i phục sự c&amp;acirc;n bằng v&amp;agrave; mang lại sự b&amp;igrave;nh an v&amp;agrave; thoải m&amp;aacute;i trong khi gi&amp;uacute;p c&amp;aacute;c đường nhăn v&amp;agrave; nếp nhăn xuất hiện mượt m&amp;agrave; hơn.&lt;br /&gt;\r\nChứa hương liệu, tinh dầu miễn ph&amp;iacute; v&amp;agrave; được thử nghiệm da liễu. Bạn sẽ được chăm s&amp;oacute;c kỹ lưỡng.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;L&amp;agrave;m sao để &amp;aacute;p dụng&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&amp;Aacute;p dụng để l&amp;agrave;m sạch mặt, cổ v&amp;agrave; ngực. Sử dụng v&amp;agrave;o buổi s&amp;aacute;ng v&amp;agrave; ban đ&amp;ecirc;m hoặc l&amp;uacute;c những dấu hiệu đầu ti&amp;ecirc;n g&amp;acirc;y k&amp;iacute;ch ứng. C&amp;oacute; thể được sử dụng ri&amp;ecirc;ng hoặc dưới sản phẩm dưỡng ẩm.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;Th&amp;agrave;nh phần&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Aqua (water), Aloe barbadenis leaf juice, Cetearyl olivate (and) sorbitan olivate, Phragmites kharka extract and (water) (and) poria cocos extract, Prunus amygdalus dulcis oil, Chamomilla recutita flower extract, Olea europaea fruit oil, Oenothera biennis seed oil, Calendula officinalis flower extract (and) glycine soja (soybean) oil, Camellia sinensis leaf extract, Xanthan gum, Rosmarinus officinalis leaf extract, Citric acid, Dehydroacetic acid (and) benzyl alcohol&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Theo thời gian, những thay đổi nhỏ c&amp;oacute; thể xảy ra với danh s&amp;aacute;ch th&amp;agrave;nh phần n&amp;agrave;y. Lu&amp;ocirc;n lu&amp;ocirc;n tham khảo c&amp;aacute;c sản phẩm bao b&amp;igrave;.&lt;br /&gt;\r\nĐể xem những sản phẩm tốt nhất cho bạn, h&amp;atilde;y thử chọn Skincare của ch&amp;uacute;ng t&amp;ocirc;i.&lt;/span&gt;&lt;br /&gt;\r\n&amp;nbsp;&lt;/p&gt;\r\n', 'Làm dịu các rào cản làn da bị suy yếu và làm dịu mong manh, làn da bị kích thích hoặc không dung nạp với huyết thanh cứu nguy cực kỳ nhẹ nhàng này. Cung cấp ánh sáng và giữ ẩm hiệu quả...', 'Very Gentle Calming Serum, Trilogy, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp', 'Very Gentle Calming Serum, Trilogy, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp');
INSERT INTO `oc_product_description` VALUES ('190', '2', 'Vital Moisturising Cream (60ml)', '&lt;p&gt;&lt;span style=&quot;font-size:18px&quot;&gt;&lt;span style=&quot;color:rgb(255, 0, 153)&quot;&gt;Lợi &amp;iacute;ch&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Một loại kem dưỡng ẩm hiệu suất linh hoạt cho tất cả c&amp;aacute;c loại da. Kh&amp;ocirc;ng nhờn v&amp;agrave; nhanh ch&amp;oacute;ng hấp thụ, n&amp;oacute; cung cấp chất dinh dưỡng ngay lập tức v&amp;agrave; giữ ẩm trong khi gi&amp;uacute;p l&amp;agrave;m mịn sự xuất hiện của đường nhăn v&amp;agrave; nếp nhăn.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;L&amp;agrave;m thế n&amp;agrave;o để &amp;aacute;p dụng&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&amp;Aacute;p dụng để l&amp;agrave;m sạch mặt, cổ v&amp;agrave; ngực.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;Th&amp;agrave;nh phần&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Aqua (water), Glycerin (vegetable), Prunus amygdalus dulcis oil, Glyceryl stearate, Oenothera biennis seed oil, Simmondsia Chinensis (Jojoba) Seed Oil, Cetearyl wheat straw glycosides (and) cetearyl alcohol, Rosa Canina (Rosehip) Seed Oil, Solanum Lycopersicum (Tomato) Seed Oil (and) Caprylic/Capric Triglyceride (and) Vaccinium Macrocarpon (Cranberry) Seed Oil (and) Helianthus Annuus Seed Oil (and) Tocopherol, Lycopene, Euterpe Oleracea (Acai) Fruit Oil (and) Tocopherol, Avena Sativa (Oat) Kernel Extract , Sclerocarya birrea seed oil, Aloe barbadenis leaf extract, Tocopheryl acetate, Daucus carota sativa seed oil, Chamomilla recutita flower extract, Citrus auranitum amara flower oil, Rosmarinus officinalis leaf extract, Sodium stearoyl glutamate, Citric acid, Dehydroacetic acid (and) benzyl alcohol, Parfum, Citral, Geraniol, Citronellol, Limonene, Linalool&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Theo thời gian, những thay đổi nhỏ c&amp;oacute; thể xảy ra với danh s&amp;aacute;ch th&amp;agrave;nh phần n&amp;agrave;y. Lu&amp;ocirc;n lu&amp;ocirc;n tham khảo c&amp;aacute;c sản phẩm bao b&amp;igrave;.&lt;br /&gt;\r\nĐể xem những sản phẩm tốt nhất cho bạn, h&amp;atilde;y thử chọn Skincare của ch&amp;uacute;ng t&amp;ocirc;i.&lt;/span&gt;&lt;br /&gt;\r\n&amp;nbsp;&lt;/p&gt;\r\n', 'Một loại kem dưỡng ẩm hiệu suất linh hoạt cho tất cả các loại da. Không nhờn và nhanh chóng hấp thụ, nó cung cấp chất dinh dưỡng ngay lập tức và giữ ẩm trong khi giúp làm mịn sự xuất hiện của đường nhăn và nếp nhăn...', 'Vital Moisturising Cream (60ml), Trilogy, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp', 'Vital Moisturising Cream (60ml), Trilogy, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹpẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp');
INSERT INTO `oc_product_description` VALUES ('77', '2', '&quot;un&quot; cover up 00', '&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;strong&gt;M&amp;atilde; m&amp;agrave;u&amp;nbsp;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;strong&gt;00&lt;/strong&gt;: t&amp;ocirc;ng m&amp;agrave;u s&amp;aacute;ng nhất của m&amp;agrave;u trắng tuyết&amp;nbsp;&lt;br /&gt;\r\n&lt;strong&gt;11&lt;/strong&gt;: m&amp;agrave;u nhạt với nền m&amp;agrave;u v&amp;agrave;ng tinh tế&amp;nbsp;&lt;br /&gt;\r\n&lt;strong&gt;22&lt;/strong&gt;: sản phẩm b&amp;aacute;n chạy nhất với nền v&amp;agrave;ng, t&amp;ocirc;ng m&amp;agrave;u n&amp;agrave;y hợp nhất do l&amp;agrave;n da s&amp;aacute;ng vừa.&amp;nbsp;&lt;br /&gt;\r\n&lt;strong&gt;33&lt;/strong&gt;: da m&amp;agrave;u s&amp;aacute;ng nhẹ&amp;nbsp;&lt;br /&gt;\r\n&lt;strong&gt;44&lt;/strong&gt;: m&amp;agrave;u da tối hơn, c&amp;oacute; thể được sử dụng để tạo hiệu ứng m&amp;agrave;u da r&amp;aacute;m nắng hoặc tạo khối cho khu&amp;ocirc;n mặt.&amp;nbsp;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Sản phẩm được nghi&amp;ecirc;n cứu để t&amp;ocirc;n l&amp;ecirc;n v&amp;agrave; phục hồi l&amp;agrave;n da khỏe đẹp, chứ kh&amp;ocirc;ng đơn thuần l&amp;agrave; che đi khuyết điểm. Ho&amp;agrave;n to&amp;agrave;n nhẹ v&amp;agrave; dưỡng ẩm cho da, sản phẩm &amp;quot;un&amp;quot; cover-up sẽ t&amp;ocirc;n l&amp;ecirc;n vẻ trẻ trung v&amp;agrave; tươi s&amp;aacute;ng cho l&amp;agrave;n da, thu nhỏ lỗ ch&amp;acirc;n l&amp;ocirc;ng v&amp;agrave; trẻ h&amp;oacute;a l&amp;agrave;n da. Sử dụng như kem nền hoặc kem che khuyết điểm v&amp;igrave; kem thấm v&amp;agrave;o da một c&amp;aacute;ch r&amp;otilde; rệt gi&amp;uacute;p che đi những khuyết điểm v&amp;agrave; đốm đỏ. Thậm ch&amp;iacute; kem c&amp;ograve;n đem lại một vẻ ngo&amp;agrave;i lung linh m&amp;agrave; kh&amp;ocirc;ng lo lớp trang điểm sẽ tr&amp;ocirc;ng qu&amp;aacute; nặng nề hoặc kh&amp;o