INSERT INTO `oc_product_description` VALUES ('238', '2', 'MP Soap White', '&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;color:#ff0066&quot;&gt;C&amp;ocirc;ng dụng:&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;D&amp;agrave;nh cho người c&amp;oacute; l&amp;agrave;n da thường hoặc kh&amp;ocirc;.&amp;nbsp;Da nhờn dầu hạn chế&amp;nbsp;sử dụng hoặc n&amp;ecirc;n&amp;nbsp;điều chỉnh th&amp;agrave;nh phần nguy&amp;ecirc;n liệu hợp l&amp;iacute;.&amp;nbsp;Gi&amp;uacute;p s&amp;aacute;ng tạo cũng như cho người c&amp;oacute; nhu cầu tự tạo ra nguy&amp;ecirc;n liệu tắm rửa hợp với l&amp;agrave;n&amp;nbsp;da m&amp;igrave;nh nhất.&amp;nbsp;Tinh dầu v&amp;agrave; chất nhờn cơ bản c&amp;oacute; sẵn trong MP Soap sẽ gi&amp;uacute;p cho c&amp;aacute;c nguy&amp;ecirc;n liệu kh&amp;aacute;c&amp;nbsp;h&amp;ograve;a quyện dễ d&amp;agrave;ng hơn, đem tới l&amp;agrave;n da mềm v&amp;agrave; trắng s&amp;aacute;ng hơn cho người sử dụng.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Hướng dẫn sử dụng&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;1. Cắt MP Soap th&amp;agrave;nh từng vi&amp;ecirc;n nhỏ cỡ 3cm, sau đ&amp;oacute; cho v&amp;agrave;o l&amp;ograve; vi s&amp;oacute;ng.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;2. L&amp;agrave;m tan chảy MP Soap trong l&amp;ograve; vi s&amp;oacute;ng (khoảng 20 gi&amp;acirc;y cho l&amp;ograve; 500W), l&amp;agrave;m n&amp;oacute;ng cho tới&amp;nbsp;khi MP Soap tan chảy ho&amp;agrave;n to&amp;agrave;n. (Khi lấy ra cẩn thận bỏng)&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;3. Cho c&amp;aacute;c nguy&a