INSERT INTO `oc_product_description` VALUES ('73', '2', '&quot;un&quot; cover up 44', '&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;strong&gt;M&amp;atilde; m&amp;agrave;u&amp;nbsp;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;strong&gt;00&lt;/strong&gt;: t&amp;ocirc;ng m&amp;agrave;u s&amp;aacute;ng nhất của m&amp;agrave;u trắng tuyết&amp;nbsp;&lt;br /&gt;\r\n&lt;strong&gt;11&lt;/strong&gt;: m&amp;agrave;u nhạt với nền m&amp;agrave;u v&amp;agrave;ng tinh tế&amp;nbsp;&lt;br /&gt;\r\n&lt;strong&gt;22&lt;/strong&gt;: sản phẩm b&amp;aacute;n chạy nhất với nền v&amp;agrave;ng, t&amp;ocirc;ng m&amp;agrave;u n&amp;agrave;y hợp nhất do l&amp;agrave;n da s&amp;aacute;ng vừa.&amp;nbsp;&lt;br /&gt;\r\n&lt;strong&gt;33&lt;/strong&gt;: da m&amp;agrave;u s&amp;aacute;ng nhẹ&amp;nbsp;&lt;br /&gt;\r\n&lt;strong&gt;44&lt;/strong&gt;: m&amp;agrave;u da tối hơn, c&amp;oacute; thể được sử dụng để tạo hiệu ứng m&amp;agrave;u da r&amp;aacute;m nắng hoặc tạo khối cho khu&amp;ocirc;n mặt.&amp;nbsp;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Sản phẩm được nghi&amp;ecirc;n cứu để t&amp;ocirc;n l&amp;ecirc;n v&amp;agrave; phục hồi l&amp;agrave;n da khỏe đẹp, chứ kh&amp;ocirc;ng đơn thuần l&amp;agrave; che đi khuyết điểm. Ho&amp;agrave;n to&amp;agrave;n nhẹ v&amp;agrave; dưỡng ẩm cho da, sản phẩm &amp;quot;un&amp;quot; cover-up sẽ t&amp;ocirc;n l&amp;ecirc;n vẻ trẻ trung v&amp;agrave; tươi s&amp;aacute;ng cho l&amp;agrave;n da, thu nhỏ lỗ ch&amp;acirc;n l&amp;ocirc;ng v&amp;agrave; trẻ h&amp;oacute;a l&amp;agrave;n da. Sử dụng như kem nền hoặc kem che khuyết điểm v&amp;igrave; kem thấm v&amp;agrave;o da một c&amp;aacute;ch r&amp;otilde; rệt gi&amp;uacute;p che đi những khuyết điểm v&amp;agrave; đốm đỏ. Thậm ch&amp;iacute; kem c&amp;ograve;n đem lại một vẻ ngo&amp;agrave;i lung linh m&amp;agrave; kh&amp;ocirc;ng lo lớp trang điểm sẽ tr&amp;ocirc;ng qu&amp;aacute; nặng nề hoặc kh&amp;o