INSERT INTO `oc_product_description` VALUES ('115', '2', 'gift card', '&lt;div style=&quot;text-align: left;&quot;&gt;&lt;span style=&quot;font-size:14px&quot;&gt;Shopping for someone else but not sure what to give them? Give them the gift of choice with an RMS Beauty Gift Card.&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:14px&quot;&gt;Gift cards are delivered by email and contain instructions to redeem them at checkout. Our gift cards have no additional processing fees.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:14px&quot;&gt;REDEEMABLE ONLY AT RMSBEAUTY.COM&lt;/span&gt;&lt;/p&gt;\r\n', 'Shopping for someone else but not sure what to give them? Give them the gift of choice with an RMS Beauty Gift Card.', 'Gift Card, RSM Beauty, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp', 'Gift Card, RSM Beauty, Makeup, Làm đẹp, Chăm sóc da, Mỹ phẩm, Kem dưỡng ẩm, Sản phẩm làm đẹp');
