INSERT INTO `oc_product_description` VALUES ('224', '2', 'Komeyu rice oil', '&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;Hướng dẫn sử dụng:&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-size:16px&quot;&gt; &amp;nbsp;Lấy một lượng vừa đủ với v&amp;ugrave;ng c&amp;oacute; nhu cầu sử dụng rồi xoa nhẹ l&amp;ecirc;n.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:18px&quot;&gt;&lt;span style=&quot;color:rgb(255, 0, 153)&quot;&gt;Th&amp;agrave;nh phần ch&amp;iacute;nh:&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-size:16px&quot;&gt; Chỉ sử dụng c&amp;aacute;c th&amp;agrave;nh phần từ gạo được sản xuất trong nước Nhật.&lt;br /&gt;\r\n&amp;bull;&amp;nbsp;&amp;nbsp; &amp;nbsp;C&amp;aacute;m gạo v&amp;agrave; hạt gạo.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Cung cấp tinh chất mầm gạo v&amp;agrave; Vitamin E rất tốt cho qu&amp;aacute; tr&amp;igrave;nh chống l&amp;atilde;o h&amp;oacute;a, cũng như l&amp;agrave;m mềm mịn tế b&amp;agrave;o da.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Bạn c&amp;oacute; biết gạo kh&amp;ocirc;ng chỉ đơn thuần l&amp;agrave; nguy&amp;ecirc;n liệu nấu ăn?&amp;nbsp;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;- Gạo vẫn lu&amp;ocirc;n l&amp;agrave; nguy&amp;ecirc;n liệu ch&amp;iacute;nh trong bữa ăn của người Nhật từ mu&amp;ocirc;n đời nay. Mỗi hạt gạo chứa nhiều tinh hoa dinh dưỡng m&amp;agrave; kh&amp;ocirc;ng chỉ đơn giản l&amp;agrave; nguy&amp;ecirc;n liệu nấu ăn. Khi chuyển thể v&amp;agrave; chiết xuất ra tinh chất để phục vụ cho nhu cầu l&amp;agrave;m đẹp, kết quả cho thấy sự bất ngờ với phản hồi rất tốt từ ph&amp;iacute;a người ti&amp;ecirc;u d&amp;ugrave;ng.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;- Bạn thậm ch&amp;iacute; c&amp;oacute; thể trộn Komeyu với Toner đang c&amp;oacute; sẵn ở nh&amp;agrave;. V&amp;igrave; tinh chất trong từng hạt gạo l&amp;agrave; đặc biệt, kh&amp;ocirc;ng c&amp;oacute; trong những loại mỹ phẩm kh&amp;aacute;c. Với mục đ&amp;iacute;ch đem tới sự m&amp;atilde;n nguyện tuyệt đối tr&amp;ecirc;n l&amp;agrave;n da, m&amp;aacute;i t&amp;oacute;c từng người sử dụng, qu&amp;aacute; tr&amp;igrave;nh nghi&amp;ecirc;n cứu ch&amp;uacute;ng t&amp;ocirc;i đ&amp;atilde; rất kỹ lưỡng v&amp;agrave; khắt khe để c&amp;oacute; được th&amp;agrave;nh phẩm ng&amp;agrave;y h&amp;ocirc;m nay.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#ff0099&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;C&amp;ocirc;ng dụng:&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;1.&amp;nbsp;&amp;nbsp; &amp;nbsp;C&amp;oacute; thể sử dụng l&amp;agrave;m toner dưỡng da, body lotion hay bất k&amp;igrave; v&amp;ugrave;ng da n&amp;agrave;o tr&amp;ecirc;n cơ thể&lt;br /&gt;\r\n2.&amp;nbsp;&amp;nbsp; &amp;nbsp;Dưỡng g&amp;oacute;t ch&amp;acirc;n, khuỷa tay ch&amp;acirc;n, đầu gối gi&amp;uacute;p mềm mịn bớt chai.&amp;nbsp;&lt;br /&gt;\r\n3.&amp;nbsp;&amp;nbsp; &amp;nbsp;Dưỡng m&amp;oacute;ng tay m&amp;oacute;ng ch&amp;acirc;n&lt;br /&gt;\r\n4.&amp;nbsp;&amp;nbsp; &amp;nbsp;Dưỡng mềm mượt t&amp;oacute;c, gi&amp;uacute;p t&amp;oacute;c mau d&amp;agrave;i.&lt;/span&gt;&lt;br /&gt;\r\n&amp;nbsp;&lt;/p&gt;\r\n', '', '', '');
