INSERT INTO `oc_product_description` VALUES ('231', '4', 'Butterbur Lotion', '&lt;p&gt;&lt;span style=&quot;color:#ff0066&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Lợi &amp;iacute;ch&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Với chiết xuất c&amp;acirc;y bơ gai, Butterbur cung cấp chất ẩm cho da, điều trị những vết nứt da tệ nhất v&amp;agrave; đem lại l&amp;agrave;n da ho&amp;agrave;n hảo sau qu&amp;aacute; tr&amp;igrave;nh sử dụng l&amp;acirc;u d&amp;agrave;i.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#ff0066&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;L&amp;agrave;m thế n&amp;agrave;o để &amp;aacute;p dụng&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Sau khi tắm hoặc rửa sạch mặt, lấy lượng vừa đủ rat ay, xoa nhẹ l&amp;ecirc;n v&amp;ugrave;ng mặt để dưỡng ẩm hoặc bất k&amp;igrave; v&amp;ugrave;ng da n&amp;agrave;o bị kh&amp;ocirc;, nẻ.&lt;/p&gt;\r\n\r\n&lt;p&gt;Lắc nhẹ trước khi d&amp;ugrave;ng.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#ff0066&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Th&amp;agrave;nh phần&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Chiết xuất đậm đặc từ l&amp;aacute; c&amp;acirc;y bơ gai cao hơn 3m,loại c&amp;acirc;y chỉ mọc tự nhi&amp;ecirc;n th&amp;agrave;nh nhiều bụi khổng lồ ở Hokkaido.&lt;/p&gt;\r\n\r\n&lt;p&gt;Theo thời gian, những thay đổi nhỏ c&amp;oacute; thể xảy ra với th&amp;agrave;nh phần dinh dưỡng n&amp;agrave;y. Lu&amp;ocirc;n lu&amp;ocirc;n tham khảo c&amp;aacute;c sản phẩm bao b&amp;igrave;.&lt;br /&gt;\r\nĐể xem những sản phẩm tốt nhất cho bạn, h&amp;atilde;y thử chọn Skincare của ch&amp;uacute;ng t&amp;ocirc;i.&lt;/p&gt;\r\n', '', '', '');
