INSERT INTO `oc_product_description` VALUES ('266', '2', 'MAMA BUTTER - BODY LOTION', '&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;color:#ff0066&quot;&gt;C&amp;ocirc;ng dụng:&lt;/span&gt; Th&amp;iacute;ch hợp cho l&amp;agrave;n da mẫn cảm, da yếu, kể cả da em b&amp;eacute;. Chống tho&amp;aacute;t nước qua lỗ ch&amp;acirc;n l&amp;ocirc;ng, dưỡng mềm da.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;C&amp;ocirc;ng dụng đặc biệt: Bạn c&amp;oacute; thể sử dụng MaMa Butter Natural Shea Butter Body Lotion để tạo n&amp;ecirc;n kem dưỡng thể của ri&amp;ecirc;ng m&amp;igrave;nh.&lt;br /&gt;\r\n&amp;bull; C&amp;ocirc;ng thức 1&lt;br /&gt;\r\nTinh chất hoa oải hương 3-6 giọt h&amp;ograve;a quyện với 30ml body lotion.&lt;br /&gt;\r\n&amp;bull; C&amp;ocirc;ng thức 2&lt;br /&gt;\r\nTinh chất cam hương 3 giọt h&amp;ograve;a quyện với 30ml body lotion.&lt;br /&gt;\r\nNgo&amp;agrave;i ra, bạn c&amp;oacute; thể s&amp;aacute;ng tạo theo &amp;yacute; th&amp;iacute;ch v&amp;agrave; nhu cầu.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;color:#ff0066&quot;&gt;Hướng dẫn sử dụng:&lt;/span&gt; B&amp;ocirc;i đều kem l&amp;ecirc;n v&amp;ugrave;ng da c&amp;oacute; nhu cầu cho tới khi lớp kem thấm hẳn, kh&amp;ocirc;ng c&amp;ograve;n thấy v&amp;acirc;n kem tr&amp;ecirc;n da nữa.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;color:#ff0066&quot;&gt;Th&amp;agrave;nh phần ch&amp;iacute;nh:&lt;/span&gt; nước, chất nhờn, squalane, sea butter.&lt;/span&gt;&lt;/p&gt;\r\n', '', '', '');
INSERT INTO `oc_product_description` VALUES ('255', '2', 'THREE RHYTHM BEAUTY OLIVE OIL &amp; FLAXSEED OIL', '&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Một trong những nguồn gốc đem lại vẻ đẹp ho&amp;agrave;n hảo cho l&amp;agrave;n da đ&amp;oacute; l&amp;agrave; c&amp;acirc;n bằng lượng dầu tr&amp;ecirc;n da.&amp;nbsp;Với c&amp;ocirc;ng thức đặc biệt h&amp;ograve;a&amp;nbsp;quyện Omega 3 v&amp;agrave; Omega 9 &amp;nbsp;trong từng vi&amp;ecirc;n thuốc, &lt;strong&gt;THREE RHYTHM BEAUTY OLIVE OIL &amp;amp; FLAXSEED OIL&lt;/strong&gt;&amp;nbsp;l&amp;agrave; sự lựa chọn tuyệt vời cho l&amp;agrave;n da kh&amp;ocirc; cần c&amp;acirc;n bằng dầu.&amp;nbsp;Với chiết xuất của những loại tinh dầu ti&amp;ecirc;u biểu nhất, bạn c&amp;oacute; thể y&amp;ecirc;n t&amp;acirc;m tin tưởng l&amp;agrave;n da v&amp;agrave;&amp;nbsp;sức khỏe của m&amp;igrave;nh khi sử dụng sản phẩm của Three. &lt;strong&gt;THREE RHYTHM BEAUTY OLIVE OIL &amp;amp; FLAXSEED OIL&lt;/strong&gt; &amp;ndash; vị vua trong những loại dầu thực&amp;nbsp;vật, được c&amp;acirc;n bằng với dầu hạt lanh &amp;ndash; đem lại sự khỏe khắn v&amp;agrave; căng tr&amp;agrave;n cho sắc đẹp cũng như&amp;nbsp;sắc khỏe của bạn. Vi&amp;ecirc;n thuốc mềm, vị dễ uống, kh&amp;ocirc;ng đắng, h&amp;atilde;y lựa&amp;nbsp;chọn &lt;strong&gt;THREE RHYTHM BEAUTY OLIVE OIL &amp;amp; FLAXSEED OIL &lt;/strong&gt;để chăm s&amp;oacute;c cho l&amp;agrave;n da của bạn.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;color:#ff0066&quot;&gt;C&amp;ocirc;ng dụng&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Olive vốn nổi tiếng l&amp;agrave; loại tr&amp;aacute;i tốt cho việc giảm c&amp;acirc;n cũng như l&amp;agrave;m đẹp da, đẹp t&amp;oacute;c. Three đ&amp;atilde; tận dụng điều n&amp;agrave;y để tạo n&amp;ecirc;n sản phẩm&amp;nbsp;thức uống chức năng n&amp;agrave;y&amp;nbsp;để phục vụ nhu cầu giảm c&amp;acirc;n, giữ số đo v&amp;oacute;c d&amp;aacute;ng chuẩn cho ph&amp;aacute;i nữ.&amp;nbsp;Ngo&amp;agrave;i ra, dầu hạt lanh c&amp;ograve;n gi&amp;uacute;p&amp;nbsp;chiết xuất ra được Omega 9 v&amp;agrave; Omega 3, đem lại nhiều chất dinh dưỡng cho cơ thể.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;color:#ff0066&quot;&gt;Hướng dẫn sử dụng&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Một ng&amp;agrave;y uống 6 vi&amp;ecirc;n.&amp;nbsp;Khi uống kh&amp;ocirc;ng cắn kh&amp;ocirc;ng nhai, nuốt thẳng với nước.&amp;nbsp;Lưu &amp;yacute; đ&amp;oacute;ng chặt nắp khi kh&amp;ocirc;ng sử dụng.&amp;nbsp;Kh&amp;ocirc;ng để ở ph&amp;ograve;ng c&amp;oacute; nhiệt độ qu&amp;aacute; cao hay nơi tiếp x&amp;uacute;c trực tiếp với &amp;aacute;nh mặt trời.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;color:#ff0066&quot;&gt;Th&amp;agrave;nh ph&amp;acirc;̀n&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Dầu olive, dầu hạt lanh, Vitamin E, chất nhờn, tinh bột.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Dinh dưỡng trong mỗi vi&amp;ecirc;n (471mg)&lt;br /&gt;\r\nEnergy: 3.2g&lt;br /&gt;\r\nProtein: 0g&lt;br /&gt;\r\nDầu:0.29g&lt;br /&gt;\r\nBột Gluxit: 0.15g&lt;br /&gt;\r\nNatri: 1.45g&lt;br /&gt;\r\nOleic Acid: 0.11g&lt;br /&gt;\r\nLinolenic Acid: 0.11g&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n', '', '', '');
INSERT INTO `oc_product_description` VALUES ('253', '2', 'THREE Point Makeup Remover', '&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Th&amp;acirc;n chai &lt;strong&gt;THREE Point Makeup Remover&lt;/strong&gt;&lt;/span&gt;&lt;span style=&quot;font-size:16px&quot;&gt; sẽ chia ra 2 lớp v&amp;agrave;ng &amp;ndash; trắng khi để y&amp;ecirc;n chai, khi lắc l&amp;ecirc;n sẽ c&amp;oacute;&amp;nbsp;m&amp;agrave;u v&amp;agrave;ng sệt sữa. Đ&amp;acirc;y l&amp;agrave; th&amp;agrave;nh quả kết tinh của hỗn hợp&amp;nbsp;dầu thực vật v&amp;agrave; tinh chất để đem lại&amp;nbsp;loại tẩy trang th&amp;agrave;nh phần tự nhi&amp;ecirc;n m&amp;agrave; hiệu quả cao nhất. Mặc kệ lớp trang điểm mắt v&amp;agrave; m&amp;ocirc;i của bạn d&amp;agrave;y hay b&amp;aacute;m như thế n&amp;agrave;o, ch&amp;uacute;ng t&amp;ocirc;i tin với nước tẩy trang của &lt;strong&gt;Three&lt;/strong&gt;, vấn ngại về tẩy&amp;nbsp;trang kh&amp;ocirc;ng sạch sẽ được x&amp;oacute;a tan. Tất nhi&amp;ecirc;n, do c&amp;oacute; th&amp;agrave;nh phần ho&amp;agrave;n&amp;nbsp;to&amp;agrave;n chiết xuất từ thi&amp;ecirc;n&amp;nbsp;nhi&amp;ecirc;n, bạn cũng kh&amp;ocirc;ng cần lo lắng l&amp;agrave;n da sẽ bị kh&amp;ocirc; sau khi tẩy, m&amp;agrave; ngược lại độ ẩm v&amp;agrave; mềm da&amp;nbsp;sẽ l&amp;agrave; điều bạn cảm nhận r&amp;otilde; r&amp;agrave;ng nhất.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;color:#ff0066&quot;&gt;C&amp;ocirc;ng dụng&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Tẩy trang sạch kể cả những v&amp;ugrave;ng trang điểm mắt v&amp;agrave; m&amp;ocirc;i đậm nhất.&amp;nbsp;Vệ sinh s&amp;acirc;u v&amp;agrave;o lỗ ch&amp;acirc;n l&amp;ocirc;ng, dưỡng ẩm, truyền nước cho da.&amp;nbsp;Sau khi sử dụng sẽ thấy da mềm r&amp;otilde; rệt.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;color:#ff0066&quot;&gt;Hướng dẫn sử dụng&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Lắc đều trước khi d&amp;ugrave;ng.&amp;nbsp;Lấy một lượng nhất định v&amp;agrave; tẩy trang v&amp;ugrave;ng cần thiết.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;color:#ff0066&quot;&gt;Th&amp;agrave;nh ph&amp;acirc;̀n&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;85% th&amp;agrave;nh phần ho&amp;agrave;n to&amp;agrave;n được lấy từ c&amp;aacute;c loại c&amp;acirc;y thi&amp;ecirc;n nhi&amp;ecirc;n,&amp;nbsp;Dầu thực vật,&amp;nbsp;Tinh dầu&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n', '', '', '');
INSERT INTO `oc_product_description` VALUES ('242', '2', 'Montmorillonite', '&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;color:#ff0066&quot;&gt;C&amp;ocirc;ng dụng&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;L&amp;agrave;m sạch bụi bẩn tr&amp;ecirc;n da, h&amp;uacute;t cặn v&amp;agrave; b&amp;atilde; nhờn trong c&amp;aacute;c lỗ ch&amp;acirc;n l&amp;ocirc;ng.&amp;nbsp;Tuy mang t&amp;iacute;nh chất l&amp;agrave;m sạch da nhưng rất nhẹ nh&amp;agrave;ng, mềm mại, kh&amp;ocirc;ng g&amp;acirc;y tổn thương da.&amp;nbsp;Ph&amp;ugrave; hợp với l&amp;agrave;n da mẫn cảm.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;color:#ff0066&quot;&gt;Hướng dẫn sử dụng&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Sử dụng để rửa mặt:&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;ol&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Lấy một muỗng to ra dĩa hoặc ch&amp;eacute;n nhỏ, pha với một muỗng c&amp;agrave; ph&amp;ecirc; nước, khuấy đều.&lt;/span&gt;&lt;/li&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Sau khi đặc sệt lại th&amp;igrave; đắp đều l&amp;ecirc;n mặt. Ch&amp;uacute; &amp;yacute; chừa c&amp;aacute;c v&amp;ugrave;ng quanh mắt v&amp;agrave; miệng, kh&amp;ocirc;ng để d&amp;acirc;y v&amp;agrave;o mắt v&amp;agrave; miệng.&lt;/span&gt;&lt;/li&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;M&amp;aacute;t xa đều tay trong khoảng 1 ph&amp;uacute;t rồi rữa sạch lại với nước.&lt;/span&gt;&lt;/li&gt;\r\n&lt;/ol&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Sử dụng l&amp;agrave;m mặt nạ:&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;ol&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Lập lại bước 1 v&amp;agrave; 2 như tr&amp;ecirc;n&lt;/span&gt;&lt;/li&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Đắp từ 3 tới 5 ph&amp;uacute;t sau đ&amp;oacute; rửa sạch lại với nước&lt;/span&gt;&lt;/li&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;C&amp;oacute; thể th&amp;ecirc;m dầu thực vật hoặc c&amp;aacute;c loại tinh dầu t&amp;ugrave;y nhu cầu chăm s&amp;oacute;c da của mỗi người để tăng cao hiệu quả.&lt;/span&gt;&lt;/li&gt;\r\n&lt;/ol&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;color:#ff0066&quot;&gt;Th&amp;agrave;nh ph&amp;acirc;̀n&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Kho&amp;aacute;ng s&amp;eacute;t Montmorillonite được khai th&amp;aacute;c sản xuất ở &amp;Yacute;&lt;/span&gt;&lt;/p&gt;\r\n', '', '', '');
