INSERT INTO `oc_product_description` VALUES ('228', '2', 'Natural &amp; Co Cleansing Milk', '&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Với kết cấu dạng sữa đặc, nỗi &amp;aacute;m ảnh tẩy trang kh&amp;ocirc;ng sạch hay qu&amp;aacute; nhiều h&amp;oacute;a chất nay đ&amp;atilde;&amp;nbsp;kh&amp;ocirc;ng c&amp;ograve;n, m&amp;agrave; đ&amp;atilde; c&amp;oacute; Nature&amp;amp;Co Cleansing Milk.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;color:#ff0066&quot;&gt;C&amp;ocirc;ng dụng&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Chuy&amp;ecirc;n dụng cho việc tẩy trang, tuy nhi&amp;ecirc;n cũng c&amp;oacute; thể sử dụng như sửa rữa mặt hằng ng&amp;agrave;y cho da.&amp;nbsp;Thử nghiệm nhiều hoạt chất c&amp;oacute; trong c&amp;aacute;c loại dầu&amp;nbsp;dưỡng da, ch&amp;uacute;ng t&amp;ocirc;i đ&amp;atilde; th&amp;agrave;nh c&amp;ocirc;ng khi đưa c&amp;aacute;c hoạt chật dưỡng da v&amp;agrave;o sửa rữa mặt, gi&amp;uacute;p tạo độ mềm mịn tối ưu cho da.&amp;nbsp;Tuy bọt rửa rất mềm mịn tinh khiết, nhưng từ lớp trang điểm tới bụi bẩn đều được l&amp;agrave;m sạch tối ưu.&amp;nbsp;Tuy độ l&amp;agrave;m&amp;nbsp;sạch rất cao nhưng kh&amp;ocirc;ng hề g&amp;acirc;y nhờn b&amp;oacute;ng da, &lt;strong&gt;Nature&amp;amp;co Cleansing Milk&lt;/strong&gt; l&amp;agrave; một trong những giải ph&amp;aacute;p tối ưu cho những l&amp;agrave;n da kh&amp;ocirc;, nứt nẻ nhưng vẫn y&amp;ecirc;u cầu sự sạch sẽ cao.&amp;nbsp;Tinh dầu Olive v&amp;agrave; tinh chất tr&amp;aacute;i bơ sẽ đem lại cho l&amp;agrave;n da độ ẩm ph&amp;ugrave; hợp.&amp;nbsp;Tinh chất từ nha đam tạo cảm gi&amp;aacute;c m&amp;aacute;t lạnh thoải m&amp;aacute;i khi massage.&amp;nbsp;M&amp;ugrave;i dịu nhẹ từ sữa v&amp;agrave; bơ rất dễ chịu v&amp;agrave; ph&amp;ugrave; hợp với mọi đối tượng.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;color:#ff0066&quot;&gt;Hướng dẫn sử dụng&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Lấy một lượng nhỏ cỡ tr&amp;aacute;i cherry ra tay, d&amp;ugrave;ng c&amp;aacute;c ng&amp;oacute;n tay xoa v&amp;agrave; massage nhẹ l&amp;ecirc;n v&amp;ugrave;ng cần tẩy trang. Sau đ&amp;oacute; rửa sạch lại với nước.Tr&amp;aacute;nh tiếp x&amp;uacute;c với mắt. Nếu c&amp;oacute; v&amp;agrave;o mắt, khuyến c&amp;aacute;o rửa sạch bằng nước thường.&amp;nbsp;Đ&amp;oacute;ng kĩ nắp chai sau khi sử dụng.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;color:#ff0066&quot;&gt;Th&amp;agrave;nh phần&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Tổng hợp c&amp;aacute;c loại tinh chất thực vật: olive, bơ, nha đam.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Đảm bảo kh&amp;ocirc;ng c&amp;oacute; h&amp;oacute;a chất như: Paraben, chất tạo m&amp;agrave;u tạo m&amp;ugrave;i, cồn.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;strong&gt;Cam kết 4 kh&amp;ocirc;ng:&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&amp;nbsp;Kh&amp;ocirc;ng chất tạo m&amp;agrave;u&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&amp;nbsp;Kh&amp;ocirc;ng Paraben&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&amp;nbsp;Kh&amp;ocirc;ng Sodium Laureth Sulfate&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&amp;nbsp;Kh&amp;ocirc;ng acid&lt;/span&gt;&lt;/p&gt;\r\n', '', '', '');
