INSERT INTO `oc_product_description` VALUES ('268', '2', 'Mama Butter - Natural Shea Butter 100%', '&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;strong&gt;Mama Butter - Natural Shea Butter 100%&lt;/strong&gt;&lt;br /&gt;\r\nSản phẩm dưỡng mặt v&amp;agrave; body d&amp;agrave;nh cho phụ nữ mang thai v&amp;agrave; cho con b&amp;uacute;.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Nhờ v&amp;agrave;o th&amp;agrave;nh phần 100 Shea Butter, Face and Body Cream của Mama Butter l&amp;agrave; sản phẩm ho&amp;agrave;n hảo cho l&amp;agrave;n da em b&amp;eacute;, cũng như l&amp;agrave;n da cực k&amp;igrave; nhạy cảm. Với mục đ&amp;iacute;ch n&amp;acirc;ng niu l&amp;agrave;n da của kh&amp;aacute;ch hang một c&amp;aacute;ch tỉ mỉ nhất, chiết xuất 100% ho&amp;agrave;n to&amp;agrave;n tự nhi&amp;ecirc;n sẽ đem tới sự ẩm, căng b&amp;oacute;ng thậm ch&amp;iacute; cho l&amp;agrave;n da kh&amp;ocirc; nhất.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;color:#ff0066&quot;&gt;C&amp;ocirc;ng dụng:&lt;/span&gt;&lt;br /&gt;\r\n&amp;bull; C&amp;oacute; thể sử dụng cho cả m&amp;oacute;ng tay, t&amp;oacute;c, g&amp;oacute;t ch&amp;acirc;n, khuỷa tay, đầu gối, mặt, to&amp;agrave;n th&amp;acirc;n..&lt;br /&gt;\r\n&amp;bull; Độ dưỡng ẩm cao.&lt;br /&gt;\r\n&lt;span style=&quot;color:#ff0066&quot;&gt;Hướng dẫn sử dụng:&lt;/span&gt;&lt;br /&gt;\r\n&amp;bull; C&amp;oacute; thể sử dụng l&amp;ecirc;n bất k&amp;igrave; v&amp;ugrave;ng n&amp;agrave;o tr&amp;ecirc;n cơ thể c&amp;oacute; nhu cầu dưỡng ẩm (mặt, to&amp;agrave;n th&amp;acirc;n, t&amp;oacute;c, tay..vv..)&lt;br /&gt;\r\n&amp;bull; C&amp;oacute; thế sử dụng bất k&amp;igrave; l&amp;uacute;c n&amp;agrave;o cảm thấy muốn dưỡng ẩm th&amp;ecirc;m.&lt;br /&gt;\r\n&amp;bull; Khi kem bị cứng lại, xoa đều ng&amp;oacute;n tay l&amp;ecirc;n, nhiệt độ cơ th&amp;ecirc; người sẽ l&amp;agrave;m kem mềm ra.&lt;br /&gt;\r\n&amp;bull; C&amp;oacute; thể sử dụng l&amp;agrave;m bước dưỡng da sau c&amp;ugrave;ng v&amp;agrave;o ban đ&amp;ecirc;m để kh&amp;oacute;a tất cả c&amp;aacute;c bước kem dưỡng kh&amp;aacute;c lại tr&amp;ecirc;n da.&lt;br /&gt;\r\n&lt;span style=&quot;color:#ff0066&quot;&gt;Th&amp;agrave;nh phần:&lt;/span&gt; 100% Shea Butter.&lt;/span&gt;&lt;/p&gt;\r\n', '', '', '');
INSERT INTO `oc_product_description` VALUES ('270', '2', 'Natural Baby Balm', '&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;strong&gt;Kem chống hăm Natural baby balm&lt;/strong&gt; với chiết xuất thi&amp;ecirc;n nhi&amp;ecirc;n, gần gũi với con người sẽ gi&amp;uacute;p cho c&amp;aacute;c b&amp;agrave; mẹ c&amp;oacute; t&amp;acirc;m l&amp;yacute; an t&amp;acirc;m hơn khi sử dụng sản phẩm để chăm s&amp;oacute;c l&amp;agrave;n da cho m&amp;igrave;nh.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;Kh&amp;ocirc;ng chỉ chống hăm, chống kh&amp;ocirc; nứt m&amp;agrave; c&amp;ograve;n dưỡng ẩm cung cấp nước cho da. Gi&amp;uacute;p l&amp;agrave;n da của trẻ c&amp;oacute; dinh dưỡng v&amp;agrave; sức khỏe để chống lại c&amp;aacute;c loại mầm mống bệnh.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;color:#ff0066&quot;&gt;Hướng dẫn sử dụng:&lt;/span&gt;&lt;br /&gt;\r\n- Lấy lượng vừa đủ ra tay, xoa đều l&amp;ecirc;n v&amp;ugrave;ng da muốn dưỡng ẩm cho tới khi kem tan ra thấm đều.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;color:#ff0066&quot;&gt;- Đặc biệt hiệu quả trong những trường hợp sau:&lt;/span&gt;&lt;br /&gt;\r\n&amp;bull; Dưỡng da hằng ng&amp;agrave;y cho em b&amp;eacute;&lt;br /&gt;\r\n&amp;bull; Khắc phục nứt nẻ, kh&amp;ocirc; da g&amp;oacute;t tay ch&amp;acirc;n, da m&amp;ocirc;ng cho em b&amp;eacute;&lt;br /&gt;\r\n&amp;bull; Dưỡng da kh&amp;ocirc; cho phụ nữ đang mang thai v&amp;agrave; cho con b&amp;uacute;.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;span style=&quot;color:#ff0066&quot;&gt;Th&amp;agrave;nh phần ch&amp;iacute;nh:&lt;/span&gt; nước, beeswax, tinh dầu olive, chiết xuất hoa oải hương, tinh dầu hoa oải hương, Vitamin E từ đậu n&amp;agrave;nh, ethanol, bidens pilosa.&amp;nbsp;&lt;/span&gt;&lt;/p&gt;\r\n', '', '', '');
